import { GatheringService } from "./gathering.service";
import { ruleOptions } from "./gathering.config";
import { PAYMENT_TYPES } from '@/constants/pay';
export default {
  name: 'ModalSoldDealGathering',
  bem: {
    gathering: 'modal-sold-deal-gathering'
  },
  serviceProviders: function serviceProviders() {
    return [GatheringService];
  },
  serviceInject: function serviceInject() {
    return {
      gatheringService: GatheringService
    };
  },
  rxState: function rxState() {
    return {
      info: this.gatheringService.info$,
      loading: this.gatheringService.loading$,
      paymentMethodList: this.gatheringService.paymentMethodList$
    };
  },
  data: function data() {
    var form = this.$stForm.create();
    var decorators = form.decorators(ruleOptions);
    return {
      form: form,
      decorators: decorators,
      show: false,
      currentPaymentType: -1,
      description: ''
    };
  },
  // 订单id，签单类型
  props: ['order_id', 'type'],
  computed: {
    isShowDeposit: function isShowDeposit() {
      return this.currentPaymentType === PAYMENT_TYPES.DEPOSIT;
    },
    depositList: function depositList() {
      return (this.paymentMethodList.filter(function (paymentMethod) {
        return paymentMethod.payment_type === PAYMENT_TYPES.DEPOSIT;
      })[0] || {}).deposit || [];
    }
  },
  created: function created() {
    var _this = this;

    this.gatheringService.getPaymentInfo(this.order_id, this.type).subscribe(function (res) {
      _this.form.setFieldsValue({
        price: parseFloat(res.info.remain_amount)
      });

      _this.gatheringService.getPaymentMethodList(_this.order_id).subscribe(function (res) {
        var paymentType = res.list[0] && res.list[0].payment_type;

        _this.form.setFieldsValue({
          payment_type: paymentType
        });

        _this.currentPaymentType = paymentType;
      });
    });
  },
  filters: {
    dealMaxNumber: function dealMaxNumber(value) {
      if (!value) return;
      return Number(value.substr(0, value.length - 1));
    }
  },
  methods: {
    // 关闭modal
    onCancel: function onCancel() {
      this.show = false;
      this.$emit('cancel');
    },
    onPaymentTypeChange: function onPaymentTypeChange(e) {
      this.currentPaymentType = e.target.value;
    },
    onSubmit: function onSubmit(e) {
      var _this2 = this;

      e.preventDefault();
      this.form.validate().then(function (values) {
        values.order_id = _this2.order_id;
        values.description = _this2.description || undefined;

        if (_this2.type === 'venues') {
          values.price = _this2.info.order_price;
        }

        _this2.gatheringService.payTransaction(values).subscribe(function (result) {
          _this2.$emit('success', {
            type: 'pay'
          });

          _this2.show = false;
        });
      });
    }
  }
};
