import { PayService } from "./pay.service";
import { columns } from "./pay.config.ts";
import tableMixin from '@/mixins/table.mixin';
import SmsSign from "./components#/pay-sign";
import BrandSettingSmsPay from '@/views/biz-modals/brand/setting/sms/pay';
var pageName = 'page-setting-sms-pay';
export default {
  mixins: [tableMixin],
  name: 'SmsPay',
  bem: {
    bPage: pageName,
    bCount: "".concat(pageName, "-count")
  },
  serviceInject: function serviceInject() {
    return {
      payService: PayService
    };
  },
  rxState: function rxState() {
    return {
      page: this.payService.page$,
      info: this.payService.info$,
      loading: this.payService.loading$
    };
  },
  modals: {
    BrandSettingSmsPay: BrandSettingSmsPay
  },
  data: function data() {
    return {
      isShowSmsSetting: true
    };
  },
  computed: {
    columns: columns
  },
  components: {
    SmsSign: SmsSign
  },
  created: function created() {},
  methods: {
    // 获取首页信息
    getSmsPayInfo: function getSmsPayInfo() {
      return this.payService.getSmsPayInfo().subscribe();
    },
    onSelectShopComplete: function onSelectShopComplete() {},
    // 短信签名
    postSmsSign: function postSmsSign(para) {
      var _this = this;

      return this.payService.postSmsSign({
        sign: para
      }).subscribe(function (res) {
        _this.getSmsPayInfo();
      });
    },
    getSmsSign: function getSmsSign(para) {
      this.postSmsSign(para);
    } // 短信购买

  }
};
