import Calendar from '@/views/biz-components/schedule/calendar';
import { PersonalTeamScheduleScheduleService } from '@/views/pages/shop/product/course/schedule/personal/service#/schedule.service';
import { PersonalScheduleReserveService } from "./service#/reserve.service";
import SchedulePersonalAddReserve from '@/views/biz-modals/schedule/personal/add-reserve';
import SchedulePersonalReserveInfo from '@/views/biz-modals/schedule/personal/reserve-info';
import { cloneDeep } from 'lodash-es';
import { PersonalService } from "./personal.service";
import { TIME_UNIT } from '@/constants/course/team';
export default {
  name: 'TeamSchedule',
  serviceInject: function serviceInject() {
    return {
      personalScheduleReserveService: PersonalScheduleReserveService,
      service: PersonalService
    };
  },
  rxState: function rxState() {
    return {
      cardList: this.personalScheduleReserveService.reserveTable$,
      auth: this.service.auth$
    };
  },
  modals: {
    SchedulePersonalAddReserve: SchedulePersonalAddReserve,
    SchedulePersonalReserveInfo: SchedulePersonalReserveInfo
  },
  components: {
    Calendar: Calendar
  },
  computed: {
    startDate: function startDate() {
      return this.$searchQuery.start_date || moment().format('YYYY-MM-DD');
    },
    isDay: function isDay() {
      var start = this.$searchQuery.start_date;
      var end = this.$searchQuery.end_date;
      return start === end;
    }
  },
  data: function data() {
    return {
      TIME_UNIT: TIME_UNIT
    };
  },
  methods: {
    onAddReserve: function onAddReserve(date) {
      this.$modalRouter.push({
        name: 'schedule-personal-add-reserve',
        props: {
          time: date
        }
      });
    },
    // 查看详情
    onDetail: function onDetail(info) {
      var _this = this;

      this.$modalRouter.push({
        name: 'schedule-personal-reserve-info',
        props: {
          id: info.id
        },
        on: {
          ok: function ok(res) {
            _this.onScheduleChange();
          }
        }
      });
    },
    // 管理私教排期
    onClickSettingSchdule: function onClickSettingSchdule() {
      var requestParam = cloneDeep(this.$searchQuery);
      var weekOfday = moment().format('E');
      requestParam.time_unit = this.TIME_UNIT.TIME_WEEK;
      requestParam.date = moment().format('YYYY-MM-DD');
      requestParam.start_date = moment().subtract(weekOfday - 1, 'days').format('YYYY-MM-DD');
      requestParam.end_date = moment().add(7 - weekOfday, 'days').format('YYYY-MM-DD');
      this.$router.push({
        name: 'shop-product-course-personal-table',
        query: requestParam
      });
    },
    // 进入表格模式
    onGetTable: function onGetTable() {
      this.$router.push({
        name: 'shop-product-course-schedule-personal-personal-reserve-table',
        query: this.$searchQuery
      });
    },
    // 刷新页面
    onScheduleChange: function onScheduleChange() {
      this.$router.push({
        query: this.$searchQuery
      });
    }
  },
  watch: {
    $route: function $route(newValue, oldValue) {
      this.personalScheduleReserveService.getList(this.$searchQuery);
      this.$refs.calendar.getWeeks(this.$searchQuery.time_unit === this.TIME_UNIT.TIME_WEEK ? 'week' : '');
    }
  }
};
