import { RefundService } from "./refund.service";
import { UserService } from '@/services/user.service';
import { ruleOptions } from "./refund.config";
export default {
  name: 'ModalSoldCardRefund',
  bem: {
    refund: 'modal-sold-card-refund'
  },
  serviceProviders: function serviceProviders() {
    return [RefundService];
  },
  serviceInject: function serviceInject() {
    return {
      userService: UserService,
      refundService: RefundService
    };
  },
  rxState: function rxState() {
    return {
      refundInfo: this.refundService.refundInfo$,
      refundReasons: this.refundService.refundReasons$,
      refundChannelSaas: this.refundService.refundChannelSaas$,
      loading: this.refundService.loading$
    };
  },
  props: ['id', 'type'],
  data: function data() {
    var form = this.$stForm.create();
    var decorators = form.decorators(ruleOptions);
    return {
      form: form,
      decorators: decorators,
      show: false,
      // 客户要求退款退货（此商品变为无效）
      refundReason: 2,
      frozenPayType: this.refundChannelSaas[0].value,
      description: ''
    };
  },
  computed: {
    isDeposit: function isDeposit() {
      return this.type === 'deposit';
    },
    isMember: function isMember() {
      return this.type === 'member';
    }
  },
  created: function created() {
    this.refundService.getRefundInfo(this.id, this.type).subscribe();
  },
  methods: {
    onSubmit: function onSubmit() {
      var _this = this;

      this.form.validate().then(function (values) {
        _this.refundService.refund({
          refund_price: +values.refund_price,
          refund_reason: +_this.refundReason,
          refund_channel: +_this.frozenPayType,
          description: _this.description
        }, _this.id, _this.type).subscribe(function (res) {
          _this.$emit('success');

          _this.show = false;
        });
      });
    }
  }
};
