import StImageUpload from '@/views/biz-components/st/image-upload/image-upload.vue';
import { cloneDeep, omit } from 'lodash-es';
import CustomLink from '@/views/biz-components/custom-link/custom-link.vue';
import { H5SettingIndexService } from "../h5-setting-index.service";
import { MessageService } from '@/services/message.service';
export default {
  name: 'h5FormShopinfoComponent',
  bem: {
    module: 'h5-form-shopinfo-component'
  },
  components: {
    StImageUpload: StImageUpload,
    CustomLink: CustomLink
  },
  props: {
    value: {
      type: Object,
      default: function _default() {
        return {
          list: []
        };
      }
    },
    moduleName: {
      type: String
    },
    maxNum: {
      type: Number,
      default: 6
    }
  },
  data: function data() {
    return {
      draggableOptions: {
        filter: '.ignore-elements'
      },
      pathList: [],
      addItem: {}
    };
  },
  serviceInject: function serviceInject() {
    return {
      h5SettingIndexService: H5SettingIndexService,
      messageService: MessageService
    };
  },
  mounted: function mounted() {
    var _this = this;

    this.h5SettingIndexService.getPathList().subscribe(function (res) {
      _this.pathList = res.list;
    });
  },
  methods: {
    imageUploadChange: function imageUploadChange(fileList) {
      this.addItem = {
        image: fileList[0],
        link: this.addItem.link || {}
      };
      this.value.list.push(this.addItem);
      this.change();
      this.addItem = {};
    },
    updateImg: function updateImg(e, index) {
      if (e.length) {
        this.value.list[index].image = e[0];
        this.change();
      }
    },
    deleteImg: function deleteImg(index) {
      if (this.value.list.length < 2) {
        return this.messageService.warn({
          content: '至少添加一张轮播图'
        });
      }

      this.value.list.splice(index, 1);
      this.change();
    },
    handleLinkChange: function handleLinkChange(link, index) {
      this.value.list[index].link = link;
      this.change();
    },
    addLink: function addLink(link) {
      this.addItem['link'] = link;
    },
    change: function change() {
      this.$emit('change');
    }
  }
};
