    SoldCourseCoach: SoldCourseCoach,
    SoldCourseFreeze: SoldCourseFreeze,
    SoldCourseRefund: SoldCourseRefund,
    SoldCourseSurplusPersonal: SoldCourseSurplusPersonal,
    SoldCourseSetTimePersonal: SoldCourseSetTimePersonal,
    SoldCourseTransfer: SoldCourseTransfer,
    SoldCourseLease: SoldCourseLease,
    SoldCourseActivated: SoldCourseActivated,
    SoldCourseGiving: SoldCourseGiving,
    CommonTaskSuccessTip: CommonTaskSuccessTip,
    SoldCourseExtendDay: SoldCourseExtendDay
  },
  serviceInject: function serviceInject() {
    return {
      personalService: PersonalService
    };
  },
  rxState: function rxState() {
    return {
      loading: this.personalService.loading$,
      list: this.personalService.list$,
      page: this.personalService.page$,
      courseStatus: this.personalService.courseStatus$,
      auth: this.personalService.auth$,
      total: this.personalService.total$
    };
  },
  hooks: function hooks() {
    return {
      internalNoteActions: useInternalNoteActions(),
      soldPersonalCourseActions: soldPersonalCourseActions(this)
    };
  },
  data: function data() {
    return {
      start_time: null,
      end_time: null,
      // 结束时间面板是否显示
      endOpen: false,
      selectedRowKeys: [],
      date: []
    };
  },
  computed: {
    columns: columns,
    totalColumns: totalColumns
  },
  mounted: function mounted() {
    this.setSearchData();
  },
  methods: {
    // 额度赠送
    onGiving: function onGiving() {
      var _this = this;

      this.$modalRouter.push({
        name: 'sold-course-giving',
        props: {
          ids: this.selectedRowKeys,
          searchQuery: cloneDeep(this.$searchQuery)
        },
        on: {
          success: function success() {
            _this.successTip();
          }
        }
      });
    },
    // 延长有效期
    onExtendDays: function onExtendDays() {
      var _this2 = this;

      this.$modalRouter.push({
        name: 'sold-course-extend-day',
        props: {
          ids: this.selectedRowKeys,
          searchQuery: cloneDeep(this.$searchQuery)
        },
        on: {
          success: function success() {
            _this2.successTip();
          }
        }
      });
    },
    successTip: function successTip() {
      var _this3 = this;

      this.$modalRouter.push({
        name: 'common-task-success-tip',
        on: {
          success: function success() {
            _this3.$router.reload();

            _this3.onClear();
          }
        }
      });
    },
    disabledSelect: function disabledSelect(record) {
      if (record.course_status !== 1 && record.course_status !== 4) {
        return true;
      }
    },
    // 查询
    onSearchNative: function onSearchNative() {
      this.$searchQuery.start_time = this.date[0] ? "".concat(this.date[0].format('YYYY-MM-DD'), " 00:00:00") : '';
      this.$searchQuery.end_time = this.date[1] ? "".concat(this.date[1].format('YYYY-MM-DD'), " 23:59:59") : '';
      this.onSearch();
    },
    onSearchResetNative: function onSearchResetNative() {
      this.date = [null, null];
      this.onSearchReset();
    },
    // 设置searchData
    setSearchData: function setSearchData() {
      var start = this.$searchQuery.start_time ? cloneDeep(moment(this.$searchQuery.start_time)) : null;
      var end = this.$searchQuery.end_time ? cloneDeep(moment(this.$searchQuery.end_time)) : null;
      this.date = [start, end];
    },
    // moment
    moment: moment,
    onSelectChange: function onSelectChange(selectedRowKeys) {
      this.selectedRowKeys = selectedRowKeys;
    },
    // 冻结
    onFreeze: function onFreeze(record) {
      var _this4 = this;

      this.$modalRouter.push({
        name: 'sold-course-freeze',
        props: {
          type: 'personal',
          id: record.id
        },
        on: {
          success: function success() {
            _this4.$router.reload();
          }
        }
      });
    },
    // 取消冻结
    onUnfreeze: function onUnfreeze(record) {
      var _this5 = this;

      this.$confirm({
        title: '提示',
        content: '是否取消冻结？',
        maskClosable: true,
        onOk: function onOk() {
          return _this5.personalService.unFreeze(record.id).toPromise().then(function () {
            _this5.$router.reload();
          });
        }
      });
    },
    // 修改教练
    onEditCoach: function onEditCoach(record) {
      var _this6 = this;

      this.$modalRouter.push({
        name: 'sold-course-coach',
        props: {
          record: record
        },
        on: {
          success: function success() {
            _this6.$router.reload();
          }
        }
      });
    },
    // 修改剩余课时
    onSurplus: function onSurplus(record) {
      var _this7 = this;

      this.$modalRouter.push({
        name: 'sold-course-surplus-personal',
        props: {
          courseData: record
        },
        on: {
          success: function success() {
            _this7.$router.reload();
          }
        }
      });
    },
    // 修改剩余时间
    onSetTime: function onSetTime(record) {
      var _this8 = this;

      this.$modalRouter.push({
        name: 'sold-course-set-time-personal',
        props: {
          courseData: record
        },
        on: {
          success: function success() {
            console.log(123333);

            _this8.$router.reload();
          }
        }
      });
    },
    // 重新激活
    onActivated: function onActivated(record) {
      var _this9 = this;

      this.$modalRouter.push({
        name: 'sold-course-activated',
        props: {
          type: 'personal',
          id: record.id
        },
        on: {
          success: function success() {
            _this9.$router.reload();
          }
        }
      });
    },
    // 延长有效期
    onLease: function onLease(record) {
      var _this10 = this;

      this.$modalRouter.push({
        name: 'sold-course-lease',
        props: {
          type: 'personal',
          id: record.id
        },
        on: {
          success: function success() {
            _this10.$router.reload();
          }
        }
      });
    },
    // 转让
    onTransfer: function onTransfer(record) {
      var _this11 = this;

      this.$modalRouter.push({
        name: 'sold-course-transfer',
        props: {
          type: 'personal',
          id: record.id
        },
        on: {
          success: function success() {
            _this11.$router.reload();
          }
        }
      });
    },
    // 跳转合同
    toContract: function toContract(record) {
      var url = "".concat(window.location.origin, "/common/contract-preview?id=").concat(record.order_id);
      window.open(url);
    },
    // 退款
    onRefund: function onRefund(record) {
      var _this12 = this;

      this.$modalRouter.push({
        name: 'sold-course-refund',
        props: {
          type: 'personal',
          id: record.id
        },
        on: {
          success: function success() {
            _this12.$router.reload();
          }
        }
      });
    },
    // 详情
    onDetail: function onDetail(record) {
      this.$router.push({
        path: "/shop/sold/course/info/personal/info/operation-record",
        query: {
          id: record.id
        }
      });
    },
    onClear: function onClear() {
      this.selectedRowKeys = [];
      this.selectedRows = [];
    }
  }
};
