import { cloneDeep } from 'lodash-es';
import { UserService } from '@/services/user.service';
import { FreezeService } from "./freeze.service";
import { ruleOptions } from "./freeze.config";
export default {
  name: 'ModalSoldCardFreeze',
  serviceProviders: function serviceProviders() {
    return [FreezeService];
  },
  serviceInject: function serviceInject() {
    return {
      userService: UserService,
      freezeService: FreezeService
    };
  },
  rxState: function rxState() {
    return {
      freezeInfo: this.freezeService.freezeInfo$,
      loading: this.freezeService.loading$,
      memberPaymentlist: this.freezeService.memberPaymentlist$
    };
  },
  bem: {
    freeze: 'modal-sold-card-freeze'
  },
  props: ['id'],
  computed: {
    isFamilyCard: function isFamilyCard() {
      return this.freezeInfo.card_number_type === 2;
    }
  },
  data: function data() {
    var form = this.$stForm.create();
    var decorators = form.decorators(ruleOptions);
    return {
      form: form,
      decorators: decorators,
      show: false,
      startTime: moment(),
      endTime: null,
      frozen_fee: null
    };
  },
  created: function created() {
    this.freezeService.getFreezeInfo(this.id).subscribe();
  },
  methods: {
    // 售卖时间-end
    onChangeEndTime: function onChangeEndTime(data) {
      this.endTime = cloneDeep(data);
    },
    disabledEndDate: function disabledEndDate(endValue) {
      return endValue.valueOf() < moment().valueOf();
    },
    onFrozenChange: function onFrozenChange(data) {
      this.form.resetFields(['payType']);
    },
    onSubmit: function onSubmit() {
      var _this = this;

      this.form.validate().then(function (values) {
        _this.freezeService.freeze({
          end_time: values.end_time.format('YYYY-MM-DD HH:mm'),
          poundage: _this.frozen_fee,
          pay_method: values.pay_method,
          member_id: _this.freezeInfo.member_id
        }, _this.id).subscribe(function (res) {
          _this.$emit('success');

          _this.show = false;
        });
      });
    }
  }
};
