import { soldColums } from "./columns.config";
import { SoldService } from "./sold.service";
import tableMixin from '@/mixins/table.mixin';
export default {
  mixins: [tableMixin],
  serviceInject: function serviceInject() {
    return {
      soldService: SoldService
    };
  },
  rxState: function rxState() {
    return {
      soldInfo: this.soldService.soldInfo$,
      loading: this.soldService.loading$,
      page: this.soldService.page$,
      orderStatus: this.soldService.orderStatus$
    };
  },
  data: function data() {
    return {
      id: ''
    };
  },
  computed: {
    soldColums: soldColums
  },
  mounted: function mounted() {
    this.id = this.$searchQuery.id;
  },
  methods: {
    range: function range(start, end) {
      var result = [];

      for (var i = start; i < end; i++) {
        result.push(i);
      }

      return result;
    },
    disabledDate: function disabledDate(current) {
      return current && current.format('YYYY-MM-DD') > moment().format('YYYY-MM-DD');
    },
    canJump: function canJump(e) {
      var arr = [1, 2, 3, 4, 5];
      var product_type = e.product_type;
      return arr.includes(product_type);
    },
    // 订单编号：点击跳转至订单详情页
    goOrderDetai: function goOrderDetai(e) {
      console.log('跳转到订单详情页', e);
      this.$router.push({
        name: 'shop-finance-order-info-collection-details',
        query: {
          id: e.id
        }
      });
    },
    // 商品名称+规格名：点击跳转至商品详情页
    goCommodityDetai: function goCommodityDetai(e) {
      var product_type = e.product_type;
      var product_id = e.product_id;
      var routeMap = {
        1: {
          // 会籍卡
          name: 'shop-product-card-member-period-info',
          query: {
            id: product_id
          }
        },
        2: {
          // 私教
          name: 'shop-product-course-manage-personal-info',
          query: {
            id: product_id
          }
        },
        3: {
          // 团教
          name: 'brand-product-course-manage-team-info',
          query: {
            courseId: product_id
          }
        },
        4: {
          // 课程包
          name: 'shop-product-course-manage-package-info-unlimit-package',
          query: {
            id: product_id
          }
        },
        5: {
          // 储值卡
          name: 'shop-product-card-deposit-info',
          query: {
            id: product_id
          }
        }
      };
      this.$router.push(routeMap[product_type]);
    },
    onChooseDate: function onChooseDate(e) {
      this.$router.push({
        query: {
          id: this.id,
          order_time_first: moment(e[0]).format('YYYY-MM-DD'),
          order_time_last: moment(e[1]).format('YYYY-MM-DD')
        }
      });
    }
  }
};
