import { EditService } from "../../edit.service";
import { MessageService } from '@/services/message.service'; // TODO: fragments迁移到biz-components

import StSelectCourseCategory from '@/views/fragments/course/select-course-category';
import StSelectTrainingAim from '@/views/fragments/course/select-training-aim';
import StImageUpload from '@/views/biz-components/st/image-upload/image-upload.vue';
import { ruleOptions } from "../create-personal-course.config";
export default {
  name: 'create-personal-course',
  serviceInject: function serviceInject() {
    return {
      editService: EditService,
      messageService: MessageService
    };
  },
  rxState: function rxState() {
    return {
      loading: this.editService.loading$
    };
  },
  components: {
    StSelectCourseCategory: StSelectCourseCategory,
    StSelectTrainingAim: StSelectTrainingAim,
    StImageUpload: StImageUpload
  },
  props: {
    info: {
      type: Object,
      default: function _default() {
        return {};
      }
    }
  },
  data: function data() {
    var form = this.$stForm.create();
    var decorators = form.decorators(ruleOptions);
    return {
      form: form,
      decorators: decorators,
      fileList: []
    };
  },
  mounted: function mounted() {
    var _this = this;

    this.$nextTick(function () {
      _this.setFieldsValue();
    });
  },
  methods: {
    save: function save(e) {
      var _this2 = this;

      e.preventDefault();
      this.form.validateFields().then(function () {
        var data = _this2.getData();

        console.log(data);

        _this2.editService.updateCourse(data).subscribe(function (res) {
          _this2.messageService.success({
            content: '提交成功'
          });

          _this2.$emit('goNext');
        });
      });
    },
    onImgChange: function onImgChange(fileList) {
      this.form.setFieldsValue({
        image: fileList[0]
      });
      console.log(fileList[0]);
    },
    onCourseTypeChange: function onCourseTypeChange(category_id) {
      this.form.setFieldsValue({
        category_id: category_id
      });
    },
    onTrainingAimChange: function onTrainingAimChange(train_aim) {
      this.form.setFieldsValue({
        train_aim: train_aim
      });
    },
    onCourseNameChange: function onCourseNameChange(e) {
      this.$emit('onCourseNameChange', e.target.value);
    },
    setFieldsValue: function setFieldsValue() {
      var info = this.info;
      this.form.setFieldsValue({
        course_name: info.course_name,
        category_id: info.category_id,
        train_aim: info.train_aim,
        duration: info.duration,
        image: info.image,
        description: info.description
      });

      if (info.image.image_key) {
        this.fileList = [info.image];
      }
    },
    getData: function getData() {
      var data = this.form.getFieldsValue();
      data.course_id = +this.$searchQuery.id;
      return data;
    }
  }
};
