import moment from 'moment';
import { cloneDeep, filter } from 'lodash-es';
import { DepositService } from "./deposit.service";
import tableMixin from '@/mixins/table.mixin';
import { columns, totalColumns } from "./deposit.config";
import StTotal from '@/views/biz-components/st/total/total.vue';
import SoldCardRefund from '@/views/biz-modals/sold/card/refund';
import SoldCardTransfer from '@/views/biz-modals/sold/card/transfer';
import SoldCardGiving from '@/views/biz-modals/sold/card/giving';
import CommonTaskSuccessTip from '@/views/biz-modals/common/task/success-tip';
import { CARD_STATUS } from '@/constants/card/deposit';
import useInternalNoteActions from '@/hooks/shop/sold-internal-note-actions.hook';
import soldDepositCardActions from '@/hooks/sold/deposit-card-actions.hook';
export default {
  name: 'PageShopSoldCardDepositList',
  mixins: [tableMixin],
  bem: {
    basic: 'page-shop-sold'
  },
  components: {
    StTotal: StTotal
  },
  modals: {
    SoldCardRefund: SoldCardRefund,
    SoldCardTransfer: SoldCardTransfer,
    SoldCardGiving: SoldCardGiving,
    CommonTaskSuccessTip: CommonTaskSuccessTip
  },
  serviceInject: function serviceInject() {
    return {
      depositService: DepositService
    };
  },
  rxState: function rxState() {
    return {
      list: this.depositService.list$,
      loading: this.depositService.loading$,
      page: this.depositService.page$,
      isValids: this.depositService.isValids$,
      auth: this.depositService.auth$,
      total: this.depositService.total$
    };
  },
  computed: {
    columns: columns,
    totalColumns: totalColumns
  },
  data: function data() {
    return {
      action: {
        /**
         * 赠送额度
         */
        giving: {
          isAvaliable: false,
          isShowTip: false
        }
      },
      is_valid: 1,
      // 结束时间面板是否显示
      endOpen: false,
      selectedRowKeys: [],
      selectedRows: [],
      date: []
    };
  },
  hooks: function hooks() {
    return {
      internalNoteActions: useInternalNoteActions(),
      soldDepositCardActions: soldDepositCardActions(this)
    };
  },
  mounted: function mounted() {
    this.setSearchData();
  },
  watch: {
    query: function query(newVal) {
      this.setSearchData();
    }
  },
  methods: {
    disabledSelect: function disabledSelect(record) {
      return false;
    },
    // 跳转合同
    toContract: function toContract(record) {
      var url = "".concat(window.location.origin, "/common/contract-preview?id=").concat(record.order_id);
      window.open(url);
    },
    // 转让
    onTransfer: function onTransfer(record) {
      var _this = this;

      this.$modalRouter.push({
        name: 'sold-card-transfer',
        props: {
          id: record.id,
          type: 'deposit'
        },
        on: {
          success: function success() {
            _this.$router.reload();
          }
        }
      });
    },
    // 退款
    onRefund: function onRefund(record) {
      var _this2 = this;

      this.$modalRouter.push({
        name: 'sold-card-refund',
        props: {
          type: 'deposit',
          id: record.id
        },
        on: {
          success: function success() {
            _this2.$router.reload();
          }
        }
      });
    },
    // 详情
    onDetail: function onDetail(record) {
      this.$router.push({
        name: "shop-sold-card-info-deposit-info-operation-record",
        query: {
          id: record.id
        }
      });
    },
    // 查询
    onSearchNative: function onSearchNative() {
      this.$searchQuery.start_time = this.date[0] ? "".concat(this.date[0].format('YYYY-MM-DD'), " 00:00:00") : '';
      this.$searchQuery.end_time = this.date[1] ? "".concat(this.date[1].format('YYYY-MM-DD'), " 23:59:59") : '';
      this.onSearch();
    },
    // 设置searchData
    setSearchData: function setSearchData() {
      this.is_valid = this.$searchQuery.is_valid;
      var start = this.$searchQuery.start_time ? cloneDeep(moment(this.$searchQuery.start_time)) : null;
      var end = this.$searchQuery.end_time ? cloneDeep(moment(this.$searchQuery.end_time)) : null;
      this.date = [start, end];
    },
    // moment
    moment: moment,
    onSelectChange: function onSelectChange(selectedRowKeys, selectedRows) {
      this.checkSelectedRows(selectedRows, 'is_valid', 'giving', function () {
        return !selectedRows.filter(function (item) {
          return item.is_valid !== CARD_STATUS.VALID;
        }).length;
      });
      this.selectedRowKeys = selectedRowKeys;
      this.selectedRows = selectedRows;
    },
    checkSelectedRows: function checkSelectedRows(selectedRows, key, targetDataKey, checkFn) {
      var _this3 = this;

      this.action[targetDataKey].isShowTip = false;
      var isAvaliable = checkFn();
      this.action[targetDataKey].isAvaliable = selectedRows.length && isAvaliable;

      if (!isAvaliable) {
        this.action[targetDataKey].isShowTip = true;
        var timerKey = "timer_".concat(targetDataKey);
        clearTimeout(this[timerKey]);
        this[timerKey] = setTimeout(function () {
          _this3.action[targetDataKey].isShowTip = false;
        }, 2000);
      }
    },
    // 额度赠送
    onGiving: function onGiving() {
      var _this4 = this;

      this.$modalRouter.push({
        name: 'sold-card-giving',
        props: {
          ids: this.selectedRowKeys,
          type: this.selectedRows[0].card_type,
          unit: this.selectedRows[0].unit,
          searchQuery: this.$searchQuery
        },
        on: {
          success: function success() {
            _this4.successTip();
          }
        }
      });
    },
    successTip: function successTip() {
      var _this5 = this;

      this.$modalRouter.push({
        name: 'common-task-success-tip',
        on: {
          success: function success() {
            _this5.$router.reload();

            _this5.onClear();
          }
        }
      });
    },
    onClear: function onClear() {
      this.selectedRowKeys = [];
      this.selectedRows = [];
    }
  }
};
