import moment from 'moment';
import { cloneDeep, filter } from 'lodash-es';
import { EarnestService } from "./earnest.service";
import tableMixin from '@/mixins/table.mixin';
import { columns } from "./earnest.config";
import ShopFinanceRefund from '@/views/biz-modals/shop/finance/refund';
import ShopFinanceGatheringEarnestAdd from '@/views/biz-modals/shop/finance/gathering/earnest/add';
import ShopFinanceGatheringEarnestDeal from '@/views/biz-modals/shop/finance/gathering/earnest/deal';
import SoldTransaction from '@/views/biz-components/sold/transaction';
import { OssService } from '@/services/oss.service';
export default {
  name: 'PageShopFinanceGatheringEarnest',
  mixins: [tableMixin],
  bem: {
    b: 'page-shop-sold'
  },
  modals: {
    ShopFinanceRefund: ShopFinanceRefund,
    ShopFinanceGatheringEarnestAdd: ShopFinanceGatheringEarnestAdd,
    ShopFinanceGatheringEarnestDeal: ShopFinanceGatheringEarnestDeal
  },
  serviceInject: function serviceInject() {
    return {
      earnestService: EarnestService,
      ossService: OssService
    };
  },
  rxState: function rxState() {
    return {
      loading: this.earnestService.loading$,
      list: this.earnestService.list$,
      page: this.earnestService.page$,
      useStatus: this.earnestService.useStatus$,
      auth: this.earnestService.auth$
    };
  },
  computed: {
    columns: columns
  },
  components: {
    SoldTransaction: SoldTransaction
  },
  data: function data() {
    return {
      // 结束时间面板是否显示
      endOpen: false,
      selectedRowKeys: [],
      selectedRows: [],
      diffSelectedRows: [],
      date: [],
      soldDealInfo: '',
      earnestInfo: ''
    };
  },
  mounted: function mounted() {
    this.setSearchData();
  },
  watch: {
    $searchQuery: function $searchQuery() {
      this.setSearchData();
    }
  },
  methods: {
    // 查询
    onSearchNative: function onSearchNative() {
      this.$searchQuery.start_date = this.date[0] ? "".concat(this.date[0].format('YYYY-MM-DD'), " 00:00:00") : '';
      this.$searchQuery.end_date = this.date[1] ? "".concat(this.date[1].format('YYYY-MM-DD'), " 23:59:59") : '';
      this.onSearch();
    },
    // 设置searchData
    setSearchData: function setSearchData() {
      var start = this.$searchQuery.start_date ? cloneDeep(moment(this.$searchQuery.start_date)) : null;
      var end = this.$searchQuery.end_date ? cloneDeep(moment(this.$searchQuery.end_date)) : null;
      this.date = [start, end];
    },
    // moment
    moment: moment,
    // 退款
    onRefund: function onRefund(record) {
      var _this = this;

      this.$modalRouter.push({
        name: 'shop-finance-refund',
        props: {
          id: record.order_id,
          product_type: record.product_type
        },
        on: {
          success: function success() {
            _this.$router.reload();
          }
        }
      });
    },
    // 跳转合同
    toContract: function toContract(record) {
      var url = "".concat(window.location.origin, "/common/contract-preview?id=").concat(record.order_id);
      this.ossService.openNewTab(url);
    },
    // 定金签单
    onDeal: function onDeal(record) {
      var _this2 = this;

      this.$modalRouter.push({
        name: 'shop-finance-gathering-earnest-deal',
        props: {
          id: record.order_id
        },
        on: {
          success: function success(res) {
            _this2.$refs.soldTransaction.onTransaction(res.id, res.product_type, res.member_id, res.member_name, res.member_mobile, record.id_card, record.id_card_type, record.card_num, record.physical_id);
          }
        }
      });
    },
    // 新增定金
    addEarnest: function addEarnest() {
      var _this3 = this;

      this.$modalRouter.push({
        name: 'shop-finance-gathering-earnest-add',
        props: {},
        on: {
          success: function success(res) {
            _this3.$router.reload();

            _this3.$refs.soldTransaction.payCallBack(res.orderId, res.type, 'pay');
          }
        }
      });
    }
  }
};
