import { SelectShopService } from "./select-shop.service";
import ShopSelect from '@/views/biz-modals/shop/select';
export default {
  name: 'SelectShop',
  modals: {
    ShopSelect: ShopSelect
  },
  serviceInject: function serviceInject() {
    return {
      selectService: SelectShopService
    };
  },
  rxState: function rxState() {
    return {
      loading: this.selectService.loading$
    };
  },
  props: {
    shopIds: {
      type: Array,
      default: function _default() {
        return [];
      }
    },
    disabled: {
      type: Boolean,
      default: false
    },
    groupParams: {
      type: Object,
      default: function _default() {
        return {};
      }
    },
    type: {
      type: String,
      default: ''
    }
  },
  data: function data() {
    return {
      checkedShopIds: [],
      list: [],
      group: this.groupParams
    };
  },
  mounted: function mounted() {
    this.checkedShopIds = this.shopIds;
    this.getShops(this.shopIds);
  },
  computed: {
    colspanNum: function colspanNum() {
      return this.disabled ? 4 : 5;
    }
  },
  methods: {
    onSelectShopComplete: function onSelectShopComplete(shopIds) {
      this.getShops(shopIds);
      this.checkedShopIds = shopIds;
      this.$emit('change', shopIds);
    },
    getShops: function getShops() {
      var _this = this;

      var shopIds = arguments.length > 0 && arguments[0] !== undefined ? arguments[0] : [];

      if (!shopIds.length) {
        return;
      }

      this.selectService.getShopBasic({
        shop_ids: shopIds
      }).subscribe(function (res) {
        _this.list = res.shops;
      });
    },
    delShopTableRecord: function delShopTableRecord(shopId) {
      var list = this.list,
          checkedShopIds = this.checkedShopIds;
      list.forEach(function (item, index) {
        if (item.shop_id === shopId) {
          list.splice(index, 1);
        }
      });
      checkedShopIds.forEach(function (item, index) {
        if (item === +shopId) {
          checkedShopIds.splice(index, 1);
        }
      });
      console.log('list-shop', list);
      this.list = list;
      this.checkedShopIds = checkedShopIds;
      this.$emit('change', checkedShopIds);
    },
    // 拼团逻辑，清空门店
    clearShopList: function clearShopList() {
      this.list = [];
      this.checkedShopIds = [];
      this.$emit('change', this.checkedShopIds);
    }
  }
};
