import { InfoService } from "./info.service";
import { listToTree } from '@/utils/list-to-tree.js';
export default {
  name: 'RoleInfo',
  serviceInject: function serviceInject() {
    return {
      infoService: InfoService
    };
  },
  rxState: function rxState() {
    return {
      loading: this.infoService.loading$,
      info: this.infoService.info$,
      brandList: this.infoService.brandList$,
      shopList: this.infoService.shopList$,
      dataGrantList: this.infoService.dataGrant$
    };
  },
  computed: {
    dataGrant: function dataGrant() {
      var dataGrant = this.dataGrantList[this.info.data_grant - 1].label;
      var departmentInfo = this.info.departments.map(function (element) {
        return element.department_name;
      }).join(','); // 当选择是跨部门时加上相关部门

      return this.info.data_grant === 3 ? "".concat(dataGrant, "  (").concat(departmentInfo, ")") : dataGrant;
    },
    brands: function brands() {
      return listToTree(this.brandList);
    },
    shops: function shops() {
      return listToTree(this.shopList);
    }
  }
};
