import { MessageService } from '@/services/message.service';
import { AddService } from "./add.service";
import H5Container from '@/views/biz-components/h5/h5-container';
import Steps from "./components#/step";
import { ruleOptions } from "./form.config.ts";
import { PatternService } from '@/services/pattern.service';
import StImageUpload from '@/views/components/image-upload#/image-upload.vue';
import BrandMarketingPluginAddPrize from '@/views/biz-modals/brand/marketing/plugin/add-prize';
import { cloneDeep } from 'lodash-es';
import 'swiper/dist/css/swiper.css';
import { swiper, swiperSlide } from 'vue-awesome-swiper';
import { TIMES_TYPE, CROWD_TYPE, SHARE_TYPE, STOP_SWIPER, ACTIVITY_STATUS, NOT_PRIZE_IMG_TYPE, CONDITION_TYPE } from '@/constants/marketing/lottery';
export default {
  name: 'PluginLotteryAdd',
  data: function data(vm) {
    var form = this.$stForm.create();
    var decorators = form.decorators(ruleOptions);
    return {
      form: form,
      decorators: decorators,
      TIMES_TYPE: TIMES_TYPE,
      CROWD_TYPE: CROWD_TYPE,
      SHARE_TYPE: SHARE_TYPE,
      STOP_SWIPER: STOP_SWIPER,
      ACTIVITY_STATUS: ACTIVITY_STATUS,
      NOT_PRIZE_IMG_TYPE: NOT_PRIZE_IMG_TYPE,
      columsTitlelist: ['奖品名称', '奖品类型', '可用门店（家）', '奖品数量（个）', '中奖概率 (%)', '操作'],
      info: {},
      preview: {
        title: '',
        startTime: '',
        endTime: '',
        perTimes: 0,
        totalTimes: 0,
        description: '',
        remain_points: 0
      },
      shareTitle: '',
      fileList: [],
      fileShareList: [],
      prizeList: [],
      timesType: TIMES_TYPE.DEFAULT,
      crowdType: CROWD_TYPE.DEFAULT,
      shareType: SHARE_TYPE.DEFAULT,
      isStopSwiper: STOP_SWIPER.DEFAULT,
      stepArr: [{
        title: '基础信息',
        key: 1
      }, {
        title: '抽奖规则',
        key: 2
      }, {
        title: '奖项设置',
        key: 3
      }],
      currentIndex: 0,
      curPrizeIndex: -1,
      notPrizeImgType: NOT_PRIZE_IMG_TYPE.DEFAULT,
      dateRangeVal: [],
      notPrize: {
        prize_name: '未中奖',
        prize: {
          image_url: ''
        }
      },
      sliderOptions: {
        autoplay: {
          delay: 3000,
          disableOnInteraction: false
        }
      },
      swiperOption: {
        spaceBetween: 6,
        slidesPerView: 1.05
      },
      defaultValue: [moment().format('HH:mm'), moment('11:59', 'HH:mm')],
      isShowEditTable: -1,
      prizeRate: [],
      prizeNum: [],
      // 奖品顺序配置， -2代表空， -1 代表未中奖， >=0 为奖品列表的下标， 前三项有-2代表抽奖信息不全，有空项
      prizeSort: [[-2, -2, -2, -2, -2, -2, -2, -1], [0, -2, -2, -2, -2, -2, -2, -1], [0, 1, -2, -2, -2, -2, -2, -1], // 奖品至少为三个，即以上三个仅做UI展示控制，不会传到后端，
      [0, 1, 2, -1, 0, 1, 2, -1], [0, -1, 1, -1, 2, -1, 3, -1], [0, -1, 1, -1, 2, 3, 4, -1], [0, 1, 2, -1, 3, 4, 5, -1], [0, 1, 2, 3, 4, 5, 6, -1]],
      hasFreeCondition: true,
      hasPointsCondition: false
    };
  },
  bem: {
    bPage: 'page-brand-marketing-plugin-lottery-add'
  },
  serviceInject: function serviceInject() {
    return {
      addService: AddService,
      pattern: PatternService,
      messageService: MessageService
    };
  },
  rxState: function rxState() {
    return {
      drawCondition: this.addService.drawCondition$,
      drawTimesType: this.addService.drawTimesType$,
      invitePoster: this.addService.invitePoster$,
      wheelDefault: this.addService.wheelDefault$,
      wheelTurnAround: this.addService.wheelTurnAround$,
      joinCrowdAll: this.addService.joinCrowdAll$,
      crowd: this.addService.crowd$,
      imgType: this.addService.imgType$,
      prizeInfoList: this.addService.prizeInfoList$,
      lucky: this.addService.lucky$,
      prize: this.addService.prize$,
      share: this.addService.share$,
      pluginStatus: this.addService.pluginStatus$
    };
  },
  modals: {
    BrandMarketingPluginAddPrize: BrandMarketingPluginAddPrize
  },
  components: {
    H5Container: H5Container,
    Steps: Steps,
    swiper: swiper,
    swiperSlide: swiperSlide,
    StImageUpload: StImageUpload
  },
  mounted: function mounted() {
    // this.notPrize.prize = this.lucky[0]
    if (this.$searchQuery.activity_id) {
      this.editVIew(this.$searchQuery.activity_id);
    }
  },
  computed: {
    // hasPointsCondition() {
    //   return this.form.getFieldValue('draw_condition')
    // },
    deleteBtnStatus: function deleteBtnStatus() {
      return this.$searchQuery.activity_id && this.$searchQuery.status === 1;
    },
    currentActive: function currentActive() {
      if (this.prizeList.length) return 0;
      return -1;
    },
    prizeListFilters: function prizeListFilters(value) {
      var _this = this;

      return this.prizeSort[this.prizeList.length].map(function (item, index) {
        if (item < -1) {
          return {
            prize: {
              image_url: ''
            },
            prize_name: ''
          };
        } else if (item > -1) {
          return _this.prizeList[item];
        } else {
          return {
            prize: {
              image_url: _this.notPrizeImgType === _this.NOT_PRIZE_IMG_TYPE.CUSTOM ? _this.notPrize.prize.image_url : _this.lucky[0].image_url
            },
            prize_name: _this.notPrize.prize_name
          };
        }
      });
    }
  },
  methods: {
    showEditTable: function showEditTable(index) {
      if (this.isShowEditTable === index) {
        this.prizeList[index].number = this.prizeNum[index];
        this.prizeList[index].rate = this.prizeRate[index];
        this.isShowEditTable = -1;
      } else {
        this.isShowEditTable = index;
      }
    },
    onDrawConditionChange: function onDrawConditionChange(val) {
      this.hasFreeCondition = val.includes(CONDITION_TYPE.FREE);
      this.hasPointsCondition = val.includes(CONDITION_TYPE.POINTS);
    },
    next: function next(para) {
      var _this2 = this;

      if (para.index) {
        para = para.index - 1;
      }

      if (para === 0) {
        this.currentIndex = 0;
      }

      if (para === 1) {
        this.form.validate(['activity_base.activity_name', 'activity_base.activity_description', 'activity_base.wheel_turn_around', 'activity_base.wheel_share_default', 'activity_base.share_title', 'activity_base.activity_sub_name']).then(function () {
          _this2.currentIndex = para;
        });
      }

      if (para === 2) {
        this.form.validate(['activity_base.activity_name', 'activity_base.activity_description', 'activity_base.wheel_turn_around', 'activity_base.wheel_share_default', 'activity_base.share_title', 'activity_base.activity_sub_name', 'activity_rule.join_crowd_all', 'activity_rule.draw_condition', 'activity_rule.draw_times_type', 'activity_rule.per_times', 'activity_rule.draw_points', 'activity_rule.total_times']).then(function () {
          _this2.currentIndex = para;
        });
      }
    },
    onChangeGetAvatar: function onChangeGetAvatar(imageFiles) {
      this.fileList = cloneDeep(imageFiles);
      this.notPrize.prize = this.fileList[0];
      this.$forceUpdate();
    },
    onShareChangeGetAvatar: function onShareChangeGetAvatar(imageFiles) {
      this.fileShareList = cloneDeep(imageFiles);
    },
    onSubmit: function onSubmit() {
      var _this3 = this;

      this.form.validate().then(function (value) {
        value.activity_base.start_time = _this3.preview.startTime;
        value.activity_base.end_time = _this3.preview.endTime;
        value.activity_base.share_title = _this3.shareTitle; // 选择自定义却不传图片

        value.activity_base.share_bg = _this3.shareType === SHARE_TYPE.CUSTOM ? _this3.fileShareList[0] || _this3.share[0] : _this3.share[0];
        value.activity_prizes = _this3.prizeList;
        value.activity_lucky.lucky = _this3.notPrizeImgType === NOT_PRIZE_IMG_TYPE.CUSTOM ? _this3.fileList[0] || _this3.prize[0] : _this3.lucky[0];

        if (_this3.hasFreeCondition && _this3.timesType === 1 && !value.activity_rule.per_times) {
          _this3.messageService.warn({
            content: '请填写抽奖次数'
          });

          return;
        }

        if (_this3.hasFreeCondition && _this3.timesType === 2 && !value.activity_rule.total_times) {
          _this3.messageService.warn({
            content: '请填写抽奖次数'
          });

          return;
        } // 无免费抽奖时也需要设置 默认的免费抽奖参数


        if (!_this3.hasFreeCondition) {
          value.activity_rule.per_times = 0;
          value.activity_rule.total_times = 0;
          value.activity_rule.draw_times_type = 1;
        }

        value.activity_rule.prize_sort = _this3.prizeSort[_this3.prizeList.length];

        if (_this3.$searchQuery.activity_id) {
          value.activity_id = _this3.$searchQuery.activity_id;

          _this3.addService.edit(value).subscribe(function (res) {
            _this3.$router.push('./success?id=' + res.id);
          });
        } else {
          _this3.addService.add(value).subscribe(function (res) {
            _this3.$router.push('./success?id=' + res.id);
          });
        }
      });
    },
    getTitle: function getTitle(e) {
      this.preview.title = e.target.value;
    },
    getName: function getName(e) {
      this.notPrize.prize_name = e.target.value;
    },
    getPerTimes: function getPerTimes(val) {
      this.preview.perTimes = val;
    },
    getTotalTimes: function getTotalTimes(val) {
      this.preview.totalTimes = val;
    },
    getDrawPoints: function getDrawPoints(val) {
      this.preview.drawPoints = val;
    },
    getDescription: function getDescription(val) {
      this.preview.description = val;
    },
    // 抽奖次数类型
    getCurTimesType: function getCurTimesType(e) {
      this.timesType = e.target.value;
    },
    // 人群类型
    getCurCrowdType: function getCurCrowdType(e) {
      this.crowdType = e.target.value;
    },
    // 分享类型
    getCurShareType: function getCurShareType(e) {
      this.shareType = e.target.value;
    },
    // 轮播开关
    stopSwiper: function stopSwiper(e) {
      this.isStopSwiper = e.target.value;
    },
    onDateChange: function onDateChange(data, str) {
      this.preview.startTime = str[0];
      this.preview.endTime = str[1];
    },
    getNotImgType: function getNotImgType(e) {
      this.notPrizeImgType = e.target.value;
    },
    getPrizeInfo: function getPrizeInfo(val) {
      if (this.curPrizeIndex > -1) {
        this.prizeList.splice(this.curPrizeIndex, 1, val);
      } else {
        this.prizeList.push(val);
        this.prizeRate.push(val.rate);
        this.prizeNum.push(val.number);
      }
    },
    getCurPrizeIndex: function getCurPrizeIndex(index) {
      this.curPrizeIndex = index;
    },
    disabledDate: function disabledDate(current) {
      return current && current.format('YYYY-MM-DD') < moment().format('YYYY-MM-DD');
    },
    onDelete: function onDelete(para) {
      if (this.isShowEditTable === para) {
        this.isShowEditTable = -1;
        return false;
      } else {
        this.prizeRate.splice(para, 1);
        this.prizeNum.splice(para, 1);
      }

      this.prizeList = this.prizeList.filter(function (item, index) {
        return index !== para;
      });
    },
    editTableNum: function editTableNum(val, index) {
      this.prizeNum[index] = val;
    },
    editTableRate: function editTableRate(val, index) {
      this.prizeRate[index] = val;
    },
    editVIew: function editVIew(id) {
      var _this4 = this;

      return this.addService.editVIew(id).subscribe(function (res) {
        _this4.info = res;

        _this4.$nextTick().then(function () {
          _this4.form.setFieldsValue({
            activity_base: res.activity_base,
            activity_lucky: res.activity_lucky,
            activity_prizes: res.activity_prizes,
            activity_rule: res.activity_rule
          });
        });

        _this4.dateRangeVal = [moment(res.activity_base.start_time.trim()), moment(res.activity_base.end_time.trim())];
        _this4.prizeList = res.activity_prizes;
        res.activity_prizes.forEach(function (item, index) {
          _this4.prizeNum[index] = item.number;
          _this4.prizeRate[index] = item.rate;
        });
        _this4.hasFreeCondition = res.activity_rule.draw_condition.includes(CONDITION_TYPE.FREE);
        _this4.hasPointsCondition = res.activity_rule.draw_condition.includes(CONDITION_TYPE.POINTS);
        _this4.preview.startTime = res.activity_base.start_time;
        _this4.preview.endTime = res.activity_base.end_time;
        _this4.preview.perTimes = res.activity_rule.per_times;
        _this4.preview.totalTimes = res.activity_rule.total_times;
        _this4.preview.description = res.activity_base.activity_description;
        _this4.preview.title = res.activity_base.activity_sub_name;
        _this4.notPrize.prize_name = res.activity_lucky.lucky_name;
        _this4.shareType = res.activity_base.wheel_share_default;
        _this4.timesType = res.activity_rule.draw_times_type;
        _this4.isStopSwiper = res.activity_base.wheel_turn_around;
        _this4.notPrizeImgType = res.activity_lucky.image_default;
        _this4.shareTitle = res.activity_base.share_title;
        _this4.share[0].image_id = res.activity_base.share_bg.image_id;
        res.activity_lucky.lucky && (_this4.fileList[0] = res.activity_lucky.lucky);
        _this4.fileShareList[0] = res.activity_base.share_bg;
        _this4.prize[0] = res.activity_lucky.lucky;

        if (_this4.notPrizeImgType === _this4.NOT_PRIZE_IMG_TYPE.CUSTOM) {
          _this4.notPrize.prize = res.activity_lucky.lucky;
        } else {
          _this4.notPrize.prize = _this4.lucky[0];
        }
      });
    }
  }
};
