import { InfoService } from "./info.service";
import { btnOptions } from "./info.config";
import moment from 'moment';
import SoldCardRefund from '@/views/biz-modals/sold/card/refund';
import SoldCardTransfer from '@/views/biz-modals/sold/card/transfer';
import { admissionColumns } from "../info.config";
import useInternalNoteActions from '@/hooks/shop/sold-internal-note-actions.hook';
import contractActions from '@/hooks/contract.hook';
import soldDepositCardActions from '@/hooks/sold/deposit-card-actions.hook';
export default {
  name: 'PageShopSoldCardDepositInfo',
  bem: {
    basic: 'page-shop-sold-info'
  },
  modals: {
    SoldCardRefund: SoldCardRefund,
    SoldCardTransfer: SoldCardTransfer
  },
  serviceInject: function serviceInject() {
    return {
      infoService: InfoService
    };
  },
  rxState: function rxState() {
    return {
      info: this.infoService.info$,
      loading: this.infoService.loading$,
      authTabs: this.infoService.authTabs$,
      auth: this.infoService.auth$
    };
  },
  hooks: function hooks() {
    return {
      internalNoteActions: useInternalNoteActions(),
      contractActions: contractActions(this),
      soldDepositCardActions: soldDepositCardActions(this)
    };
  },
  computed: {
    // 门店范围
    admissionColumns: admissionColumns,
    btnOptions: btnOptions
  },
  methods: {
    moment: moment,
    // 跳转合同
    toContract: function toContract() {
      var record = this.info;
      var url = "".concat(window.location.origin, "/common/contract-preview?id=").concat(record.order_id);
      window.open(url);
    },
    // 退款
    onRefund: function onRefund() {
      var _this = this;

      this.$modalRouter.push({
        name: 'sold-card-refund',
        props: {
          type: 'deposit',
          id: this.infoService.id
        },
        on: {
          success: function success() {
            _this.$router.reload();
          }
        }
      });
    },
    // 转让
    onTransfer: function onTransfer() {
      var _this2 = this;

      this.$modalRouter.push({
        name: 'sold-card-transfer',
        props: {
          id: this.infoService.id,
          type: 'deposit'
        },
        on: {
          success: function success() {
            _this2.$router.reload();
          }
        }
      });
    },
    // 对内备注
    onInternalNote: function onInternalNote() {
      this.internalNoteActions.onOpenModal({
        id: this.infoService.id
      });
    },

    /**
     * 修改合同备注
     */
    onEditContractDescription: function onEditContractDescription() {
      var _this$info = this.info,
          order_id = _this$info.order_id,
          description = _this$info.description;
      this.contractActions.editDescription({
        order_id: order_id,
        description: description
      });
    }
  }
};
