import rgbHex from 'rgb-hex';
export default {
  directives: {
    bg: function bg(el) {
      var computedStyle = window.getComputedStyle(el, null);
      setTimeout(function () {
        var bgColorRgb = computedStyle.getPropertyValue('background-color');
        var bgColorHex = rgbHex(bgColorRgb);
        el.innerHTML = el.textContent + '<br>' + '#' + bgColorHex + '<br>' + bgColorRgb;
      });
    }
  }
};
