import CreateGroupCourse from "./edit#/create-group-course";
import SetShopCoach from "./edit#/set-shop-coach";
import SetSellPrice from "./edit#/set-sell-price";
import { EditService } from "./edit.service";
export default {
  components: {
    CreateGroupCourse: CreateGroupCourse,
    // 创建小班课程
    SetShopCoach: SetShopCoach,
    // 设置上课门店及教练
    SetSellPrice: SetSellPrice // 设置售卖价格

  },
  serviceInject: function serviceInject() {
    return {
      editService: EditService
    };
  },
  rxState: function rxState() {
    return {
      info: this.editService.info$
    };
  },
  data: function data(vm) {
    return {
      currentIndex: 0,
      courseId: 0,
      courseName: '',
      stepArr: [{
        title: '基础信息',
        key: 0
      }, {
        title: "".concat(vm.$c('coach'), "\u4FE1\u606F"),
        key: 1
      }, {
        title: '售卖信息',
        key: 2
      }]
    };
  },
  methods: {
    goNext: function goNext(courseId) {
      if (courseId) {
        this.courseId = courseId;
      }

      if (this.currentIndex < 2) {
        this.currentIndex = this.currentIndex + 1;
      }
    },
    changeStep: function changeStep(step) {
      this.currentIndex = step;
    },
    onCourseNameChange: function onCourseNameChange(courseName) {
      this.courseName = courseName;
    },
    onCourseIdChange: function onCourseIdChange(courseId) {
      this.courseId = courseId;
    },
    onBack: function onBack(step) {
      this.currentIndex = step;
    }
  }
};
