      CourseService = SetShopTeamCourseService;
    }

    return {
      messageService: MessageService,
      courseService: CourseService
    };
  },
  rxState: function rxState() {
    return {
      loading: this.courseService.loading$
    };
  },
  components: {
    StSelectCourseCategory: StSelectCourseCategory,
    StSelectTrainingAim: StSelectTrainingAim,
    StImageUpload: StImageUpload
  },
  props: {
    from: {
      type: String,
      default: 'shop'
    },
    info: {
      type: Object,
      default: function _default() {
        return {};
      }
    }
  },
  data: function data() {
    var form = this.$stForm.create();
    var decorators = form.decorators(ruleOptions);
    return {
      form: form,
      decorators: decorators,
      fileList: [],
      activeBtn: 'save',
      courseId: 0
    };
  },
  mounted: function mounted() {
    var _this = this;

    this.$nextTick(function () {
      _this.setFieldsValue();
    });
  },
  methods: {
    save: function save(e) {
      this.activeBtn = e.target.dataset.name;
      e.preventDefault();
      this.doSave(this.onSubmitSuccess);
    },
    doSave: function doSave(callback) {
      var _this2 = this;

      this.form.validate().then(function () {
        var data = _this2.getData();

        _this2.courseService.setCourse(data).subscribe(function (res) {
          _this2.courseId = res.course_id;
          callback();
        });
      });
    },
    saveAndGoNext: function saveAndGoNext(e) {
      var _this3 = this;

      this.activeBtn = e.target.dataset.name;
      this.doSave(function () {
        _this3.messageService.success({
          content: '保存成功'
        });

        _this3.$emit('goNext', _this3.courseId);
      });
    },
    onSubmitSuccess: function onSubmitSuccess() {
      this.messageService.success({
        content: '保存成功'
      });
      var name = this.getParentListName();
      this.$router.push({
        name: name
      });
    },
    getParentListName: function getParentListName() {
      /**
       * 品牌团课与门店团课保存后跳到对应的列表
       */
      return {
        shop: 'shop-product-course-manage-team-list',
        brand: 'brand-product-course-team-list-brand'
      }[this.from];
    },
    onImgChange: function onImgChange(fileList) {
      this.form.setFieldsValue({
        image: fileList[0]
      });
    },
    onCourseTypeChange: function onCourseTypeChange(category_id) {
      this.form.setFieldsValue({
        category_id: category_id
      });
    },
    onTrainingAimChange: function onTrainingAimChange(train_aim) {
      this.form.setFieldsValue({
        train_aim: train_aim
      });
    },
    onCourseNameChange: function onCourseNameChange(e) {
      this.$emit('onCourseNameChange', e.target.value);
    },
    setFieldsValue: function setFieldsValue() {
      var info = this.info;
      this.form.setFieldsValue({
        course_name: info.course_name,
        category_id: info.category_id,
        train_aim: info.train_aim,
        strength_level: info.strength_level,
        calories: info.calories,
        duration: info.duration,
        price: info.price,
        image: info.image,
        description: info.description
      });

      if (info.image && info.image.image_key) {
        this.fileList = [info.image];
      }
    },
    getData: function getData() {
      var data = this.form.getFieldsValue();
      var id = this.$searchQuery.id;

      if (id) {
        data.course_id = +id;
      }

      data.price = +data.price;
      return data;
    }
  }
};
