import GroupForm from "./components#/group-form";
import { ruleOptions, cardColumns } from "./add-package-course.config";
import { AddPackageCourseService } from "./add-package-course.service";
import { UserService } from '@/services/user.service';
import { PRODUCT_TYPE } from '@/constants/marketing/group-buy';
import moment from 'moment';
import { PatternService } from '@/services/pattern.service';
export default {
  serviceInject: function serviceInject() {
    return {
      addPackageCourseService: AddPackageCourseService,
      userService: UserService,
      pattern: PatternService
    };
  },
  components: {
    GroupForm: GroupForm
  },
  rxState: function rxState() {
    return {
      loading: this.addPackageCourseService.loading$,
      shopList: this.userService.shopList$,
      courseList: this.addPackageCourseService.courseList$
    };
  },
  data: function data() {
    var form = this.$stForm.create();
    var decorators = form.decorators(ruleOptions);
    return {
      form: form,
      decorators: decorators,
      cardColumns: cardColumns,
      tableData: [],
      tableText: '',
      // 优惠设置错误提示
      tableErr: false,
      PRODUCT_TYPE: PRODUCT_TYPE
    };
  },
  methods: {
    changeShop: function changeShop(value) {
      this.addPackageCourseService.getCourseList({
        shop_id: value
      }).subscribe(function (res) {});
    },
    changeCourse: function changeCourse(value) {
      this.tableData = this.courseList.filter(function (item) {
        return item.id === value;
      })[0].product_spec;
      this.tableData[0].is_select = true;
    },
    setPriceChange: function setPriceChange() {
      if (!this.tableData[0].group_price) {
        this.tableText = '请输入拼团价格';
        this.tableErr = true;
      } else {
        this.tableText = '';
        this.tableErr = false;
      }
    },
    onSubmit: function onSubmit(data) {
      var _this = this;

      data.shop_ids = [+this.form.getFieldValue('shop_id')];
      data.product_type = PRODUCT_TYPE.PACKAGE_COURSE;
      data.product_id = this.form.getFieldValue('course_id');
      data.sku = this.tableData.map(function (item) {
        return {
          sku_id: _this.form.getFieldValue('course_id'),
          group_price: item.group_price,
          price: item.price
        };
      });
      this.addPackageCourseService.createGroupbuy(data).subscribe(function (res) {
        _this.$router.push({
          path: "./list"
        });
      });
    }
  }
};
