import moment from 'moment';
import { cloneDeep, filter } from 'lodash-es';
import { FollowService } from "./follow.service";
import tableMixin from '@/mixins/table.mixin';
import { columns } from "./follow.config";
import { MessageService } from '@/services/message.service';
export default {
  name: 'PageShopMemberFollow',
  bem: {
    bPage: 'page-shop-member-follow'
  },
  mixins: [tableMixin],
  serviceInject: function serviceInject() {
    return {
      followService: FollowService,
      messageService: MessageService
    };
  },
  rxState: function rxState() {
    return {
      loading: this.followService.loading$,
      auth: this.followService.auth$,
      list: this.followService.list$,
      page: this.followService.page$,
      staffList: this.followService.staffList$,
      coachList: this.followService.coachList$,
      operatorList: this.followService.operatorList$,
      memberLevel: this.followService.memberLevel$,
      followWayList: this.followService.followWayList$,
      followStatusList: this.followService.followStatusList$
    };
  },
  data: function data() {
    return {
      dateFormat: 'YYYY-MM-DD',
      date: []
    };
  },
  computed: {
    columns: columns
  },
  mounted: function mounted() {
    this.setSearchDate();
  },
  methods: {
    setSearchDate: function setSearchDate() {
      if (!this.$searchQuery.follow_start_date) return;
      var start = moment(this.$searchQuery.follow_start_date);
      var end = moment(this.$searchQuery.follow_end_date);
      this.date = [start, end];
    },
    onChangeSell: function onChangeSell(value) {
      this.$searchQuery.follow_salesman_id = value;
    },
    onChangeCoach: function onChangeCoach(value) {
      this.$searchQuery.follow_coach_id = value;
    },
    onChangeOperator: function onChangeOperator(value) {
      this.$searchQuery.operator_id = value;
    },
    refeshPage: function refeshPage() {
      this.$router.reload();
    },
    // 查询
    onSearchNative: function onSearchNative() {
      console.log(this.$searchQuery.follow_start_num);
      console.log(this.$searchQuery.follow_end_num);

      if (this.$searchQuery.follow_start_num > this.$searchQuery.follow_end_num && this.$searchQuery.follow_start_num !== '' && this.$searchQuery.follow_end_num !== '') {
        this.messageService.warn({
          content: '最小跟进次数要小于最大跟进次数'
        });
        return;
      }

      this.$searchQuery.follow_start_date = this.date[0] ? "".concat(this.date[0].format('YYYY-MM-DD'), " 00:00") : '';
      this.$searchQuery.follow_end_date = this.date[1] ? "".concat(this.date[1].format('YYYY-MM-DD'), " 23:59") : '';
      this.onSearch();
    },
    infoFunc: function infoFunc(record) {
      this.$router.push({
        name: 'shop-member-info-basic',
        query: {
          id: record.member.id
        }
      });
    },
    moment: moment,
    handleReset: function handleReset() {
      this.date = [null, null];
      this.onSearchReset();
    }
  }
};
