import { BindService } from "./bind.service";
import { ruleOptions } from "./bind.config";
import NoCaptcha from '@/views/biz-components/no-captcha';
import { PatternService } from '@/services/pattern.service';
import { NoCaptchaService } from '@/services/no-captcha.service';
import InputPhone from '@/views/biz-components/input-phone/input-phone';
import InputPhoneCode from '@/views/biz-components/input-phone-code/input-phone-code';
import { MessageService } from '@/services/message.service';
import { cloneDeep } from 'lodash-es';
import AccountUnbind from '@/views/biz-modals/account/unbind';
export default {
  serviceInject: function serviceInject() {
    return {
      bindService: BindService,
      messageService: MessageService,
      noCaptchaService: NoCaptchaService,
      pattern: PatternService
    };
  },
  rxState: function rxState() {
    return {
      info: this.bindService.info$,
      loading: this.bindService.loading$
    };
  },
  components: {
    NoCaptcha: NoCaptcha,
    InputPhone: InputPhone,
    InputPhoneCode: InputPhoneCode
  },
  modals: {
    AccountUnbind: AccountUnbind
  },
  computed: {
    modalTitle: function modalTitle() {
      return this.info.bind_mobile ? '更改绑定手机号' : '绑定手机号';
    }
  },
  mounted: function mounted() {
    this.bindService.fetchUserInfo().subscribe();
  },
  data: function data() {
    var form = this.$stForm.create();
    var decorators = form.decorators(ruleOptions);
    return {
      form: form,
      decorators: decorators,
      show: false,
      isCountTime: false
    };
  },
  methods: {
    onClickUnbind: function onClickUnbind() {
      this.show = false;
      this.$modalRouter.push({
        name: 'account-unbind',
        props: {},
        on: {}
      });
    },
    onClickCaptcha: function onClickCaptcha() {
      var _this = this;

      this.form.validate(['country_phone']).then(function (values) {
        var country_phone = values.country_phone;
        var params = {
          phone: country_phone.phone,
          country_code_id: country_phone.code_id,
          is_bind: 2
        };

        _this.getCaptcha(params);
      });
    },
    getCaptcha: function getCaptcha(params) {
      var _this2 = this;

      var nvc_val = this.noCaptchaService.generateNVCVal();

      if (!nvc_val) {
        return;
      }

      params.nvc_val = nvc_val;
      this.bindService.getCaptcha(params).subscribe(function (res) {
        _this2.noCaptchaService.resetNVC();

        _this2.isCountTime = true;
      }, this.errorHandler);
    },
    errorHandler: function errorHandler(err) {
      var code = err.response.code;

      if (this.noCaptchaService.testIsNeedCallCaptcha(code)) {
        this.noCaptchaService.callCaptcha(code);
        return;
      }

      this.noCaptchaService.resetNVC();
    },
    endCount: function endCount() {
      this.isCountTime = false;
    },
    onSelectCountry: function onSelectCountry(event) {
      this.countryInfo = event;
    },
    onBind: function onBind() {
      var _this3 = this;

      this.form.validate().then(function (values) {
        var params = cloneDeep(values);
        var country_phone = values.country_phone;
        params.phone = country_phone.phone;
        params.country_code_id = country_phone.code_id;
        delete params.country_phone;
        delete params.name;
        delete params.password;

        _this3.bindService.bindAccountPhone(params).subscribe(function (res) {
          _this3.show = false;

          _this3.messageService.success({
            content: '绑定手机号成功'
          });
        });
      });
    }
  }
};
