export default {
  serviceInject: function serviceInject() {
    return {
      addPersonalService: AddPersonalService,
      pattern: PatternService
    };
  },
  rxState: function rxState() {
    return {
      loading: this.addPersonalService.loading$,
      personalList: this.addPersonalService.personalList$,
      coach: this.addPersonalService.coachList$
    };
  },
  computed: {
    cardColumns: cardColumns
  },
  mounted: function mounted() {
    console.log(this.personalList);
  },
  bem: {
    basic: 'brand-marketing-group-personal'
  },
  data: function data() {
    var form = this.$stForm.create();
    var decorators = form.decorators(ruleOptions);
    return {
      form: form,
      decorators: decorators,
      selectedRowKeys: [],
      // 优惠设置选中项
      tableText: '',
      // 优惠设置错误提示
      tableErr: false,
      newCoach: [],
      groupParams: {
        type: 3,
        id: null
      }
    };
  },
  methods: {
    // 输入拼团课时
    changeHour: function changeHour(e) {
      this.newCoach.forEach(function (item, index) {
        item.hour = e.target.value;
      });
    },
    // 设置选择私教课并返回教练
    selectCourseChange: function selectCourseChange(id) {
      var _this = this;

      this.groupParams.id = id;
      this.addPersonalService.getCoachList(id).subscribe(function (res) {
        _this.newCoach = _this.coach.map(function (item) {
          return {
            is_select: false,
            level: item.spec,
            id: item.id,
            hour: '--',
            group_price: ''
          };
        });

        if (_this.newCoach.length === 1) {
          _this.selectedRowKeys = _this.newCoach;
          _this.newCoach[0].is_select = true;
        }
      });
    },
    // 优惠设置选择变化
    onChange: function onChange(value) {
      var _this2 = this;

      this.selectedRowKeys = value;
      this.newCoach.forEach(function (coach) {
        coach.is_select = _this2.selectedRowKeys.indexOf(coach.id) !== -1;
      });
    },
    // 处理输入拼团价格的逻辑
    setPriceChange: function setPriceChange() {
      var selectedCoach = this.newCoach.filter(function (item) {
        return item.is_select;
      });
      var hasEmpty = selectedCoach.filter(function (item) {
        return !item.group_price;
      });

      if (hasEmpty.length > 0) {
        this.tableText = '请输入拼团价格';
        this.tableErr = true;
      } else {
        this.tableText = '';
        this.tableErr = false;
      }
    },
    // 新建拼团活动
    onSubmit: function onSubmit(data) {
      var _this3 = this;

      console.log(data);
      if (this.tableErr) return;
      var selectedCoach = this.newCoach.filter(function (item) {
        return item.is_select;
      });

      if (selectedCoach.length === 0) {
        this.tableText = "\u8BF7\u9009\u62E9\u81F3\u5C11\u4E00\u4E2A".concat(this.$c('coach'));
        this.tableErr = true;
        return;
      }

      data.init_course_num = +this.form.getFieldValue('group_hour');
      data.product_type = 3; // 私教课

      data.product_id = +this.form.getFieldValue('course_id');
      data.sku = selectedCoach.map(function (item) {
        return {
          sku_id: item.id,
          group_price: item.group_price
        };
      });
      this.addPersonalService.createGroupbuy(data).subscribe(function (res) {
        _this3.$router.push({
          path: "/brand/marketing/plugin/group/list"
        });
      });
    }
  },
  components: {
    GroupForm: GroupForm
  }
};
