export default {
  name: 'StFormItem',
  inject: {
    stFormConfig: 'stFormConfig'
  },
  props: {
    // 用于修正label的margin-left值
    labelFix: {
      type: Boolean,
      default: false
    },

    /**
     * 表单label宽度 如88px
     */
    labelWidth: {
      type: String,
      default: ''
    },

    /**
     * 表单label右侧和表单空间的间隔 默认为24px，该属性会传递给st-form-item组件
     */
    labelGutter: {
      type: String,
      default: ''
    },
    // 类型 控制样式 text|input
    type: {
      type: String,
      default: 'input'
    }
  },
  computed: {
    computedLabelWidth: function computedLabelWidth() {
      return this.labelWidth || this.stFormConfig.labelWidth || '88px';
    },
    computedLabelGutter: function computedLabelGutter() {
      return this.labelGutter || this.stFormConfig.labelGutter || '24px';
    }
  },
  mounted: function mounted() {
    var _this = this;

    this.$nextTick().then(function () {
      var labelEl = _this.$el.querySelector('.ant-form-item-label');

      var wrapperEl = _this.$el.querySelector('.ant-form-item-control-wrapper');

      labelEl && (labelEl.style.width = _this.computedLabelWidth);
      wrapperEl && (wrapperEl.style.paddingLeft = _this.labelGutter);
    });
  }
};
