export default {
  name: 'h5FormAssetsdataComponent',
  bem: {
    module: 'h5-form-assetsdata-component'
  },
  components: {},
  props: {
    assetsData: {
      type: Object,
      default: function _default() {
        return {};
      }
    }
  },
  watch: {
    assetsData: {
      handler: function handler(newVal) {
        this.list = newVal.content.list;
      },
      immediate: true,
      deep: true
    }
  },
  data: function data() {
    return {
      list: [],
      canDrag: true
    };
  },
  methods: {
    draggChange: function draggChange() {
      this.$emit('draggChange', this.list);
    },
    onClick: function onClick(index) {
      console.log('----', this.list, index);
      var displayTotal = 0;
      this.list.forEach(function (val) {
        if (val.is_display) {
          displayTotal++;
        }
      });

      if (displayTotal === 1 && this.list[index].is_display) {
        return;
      }

      this.list[index].is_display = +!this.list[index].is_display;
      this.changeData();
    },
    changeData: function changeData() {
      this.$emit('change');
    }
  }
};
