import moment from 'moment';
import { cloneDeep } from 'lodash-es';
import { UserService } from '@/services/user.service';
import { FreezeService } from "./freeze.service";
import { OPERATION_TYPES } from '@/constants/sold/operations';
import { ruleOptions } from "./freeze.config";
export default {
  name: 'ModalSoldCourseFreeze',
  serviceProviders: function serviceProviders() {
    return [FreezeService];
  },
  serviceInject: function serviceInject() {
    return {
      userService: UserService,
      freezeService: FreezeService
    };
  },
  rxState: function rxState() {
    return {
      loading: this.freezeService.loading$,
      info: this.freezeService.info$,
      memberPaymentlist: this.freezeService.memberPaymentlist$
    };
  },
  bem: {
    freeze: 'modal-sold-course-freeze'
  },
  props: ['id', 'type'],
  data: function data() {
    var form = this.$stForm.create();
    var decorators = form.decorators(ruleOptions);
    return {
      form: form,
      decorators: decorators,
      OPERATION_TYPES: OPERATION_TYPES,
      show: false,
      startTime: moment(),
      endTime: null,
      frozen_fee: null,
      frozen_pay_type: 1
    };
  },
  created: function created() {
    var _this = this;

    this.freezeService.getInfo(this.id, this.type).subscribe(function (res) {
      _this.freezeService.getMemberPaymentList({
        member_id: res.info.member_id,
        product_type: res.info.product_type,
        operation_type: OPERATION_TYPES.FREEZE
      }).subscribe();
    });
  },
  methods: {
    moment: moment,
    // end_time validatorFn
    end_time_validator: function end_time_validator(rule, value, callback) {
      if (!value) {
        // eslint-disable-next-line
        callback('请选择冻结结束日期');
      } else {
        // eslint-disable-next-line
        callback();
      }
    },
    // pay_type validatorFn
    pay_type_validator: function pay_type_validator(rule, value, callback) {
      if (!value && this.frozen_fee > 0) {
        // eslint-disable-next-line
        callback('请选择支付方式');
      } else {
        // eslint-disable-next-line
        callback();
      }
    },
    // 售卖时间-end
    end_time_change: function end_time_change(data) {
      this.endTime = cloneDeep(data);
    },
    disabledEndDate: function disabledEndDate(endValue) {
      return endValue.valueOf() < moment().valueOf();
    },
    onFrozenChange: function onFrozenChange(data) {
      this.form.resetFields(['payType']);
    },
    onSubmit: function onSubmit() {
      var _this2 = this;

      this.form.validate().then(function (values) {
        _this2.freezeService.freeze({
          start_time: moment(_this2.info.start_time).format('YYYY-MM-DD HH:mm'),
          end_time: values.endTime.format('YYYY-MM-DD HH:mm'),
          frozen_fee: _this2.frozen_fee,
          frozen_pay_type: values.payType
        }, _this2.id, _this2.type).subscribe(function (res) {
          _this2.show = false;

          _this2.$emit('success');
        });
      });
    }
  }
};
