import { ManageService } from "./manage.service";
import { MessageService } from '@/services/message.service';
import { UserService } from '@/services/user.service';
import { PatternService } from '@/services/pattern.service';
import BrandAppVenueBasicSetting from '@/views/biz-modals/brand/app/venue/basic-setting';
import { AREA_STATUS } from '@/constants/venue';
var baseBem = 'shop-app-venue-manage';
export default {
  bem: {
    manage: baseBem,
    left: 'left',
    right: 'right'
  },
  serviceInject: function serviceInject() {
    return {
      manageService: ManageService,
      messageService: MessageService,
      userService: UserService,
      pattern: PatternService
    };
  },
  rxState: function rxState() {
    return {
      info: this.manageService.info$,
      loading: this.manageService.loading$,
      venueList: this.manageService.venueList$,
      auth: this.manageService.auth$
    };
  },
  modals: {
    BrandAppVenueBasicSetting: BrandAppVenueBasicSetting
  },
  data: function data() {
    return {
      AREA_STATUS: AREA_STATUS,
      venueInfo: {
        reserve_time: '',
        per_time: '',
        price: '',
        area_status: ''
      }
    };
  },
  computed: {
    isEdit: function isEdit() {
      return this.$searchQuery.type === 'edit';
    }
  },
  updated: function updated() {
    this.initActive();
  },
  created: function created() {
    this.initActive();
  },
  methods: {
    initActive: function initActive() {
      if (!this.venueList.length) return;
      var id = +this.$searchQuery.id;
      var area_id = +this.$searchQuery.area_id;
      var index = -1;
      if (id) index = this.venueList.findIndex(function (item) {
        return item.venues_id === id;
      });
      if (area_id) index = this.venueList.findIndex(function (item) {
        return item.id === area_id;
      });

      if (index < 0) {
        index = 0;
        this.clickVenue(this.venueList[index]);
        return;
      }

      this.setActive(this.venueList[index]);
    },
    setActive: function setActive(venue) {
      this.venueList.forEach(function (item) {
        return item.active = false;
      });
      venue.active = true;
      this.venueInfo = venue;
      this.manageService.SET_PER_TIME(venue.per_time);
    },
    clickVenue: function clickVenue(venue) {
      this.setActive(venue);
      this.$router.push({
        name: 'shop-app-venue-manage-list',
        query: {
          id: venue.venues_id
        }
      });
    },
    onClickSwitchVenue: function onClickSwitchVenue(venue) {
      var _this = this;

      var params = {
        id: venue.id,
        status: venue.area_status === AREA_STATUS.ON ? AREA_STATUS.OFF : AREA_STATUS.ON
      };
      this.manageService.switchVenue(params).subscribe(function () {
        _this.manageService.getVenueList().subscribe();
      });
    },
    onClickEditVenue: function onClickEditVenue(id) {
      this.$router.push({
        name: 'shop-app-venue-manage-edit',
        query: {
          area_id: id
        }
      });
    },
    onClickDelVenue: function onClickDelVenue(id) {
      var _this2 = this;

      this.manageService.delVenue({
        id: id
      }).subscribe(function () {
        _this2.manageService.getVenueList().subscribe(function () {
          if (!_this2.venueList.length) {
            _this2.venueInfo = {};

            _this2.$router.push({
              name: 'shop-app-venue-manage-list'
            });
          }
        });
      });
    },
    onCLickAddVenue: function onCLickAddVenue() {
      this.$router.push({
        name: 'shop-app-venue-manage-add',
        query: {
          id: this.venueInfo.venues_id
        }
      });
    }
  }
};
