import { OssService } from '@/services/oss.service';
import { AppConfig } from '@/constants/config';
import { MessageService } from '@/services/message.service';
import FaceRecognition from '@/views/biz-modals/face/recognition';
var defaultSize = {
  w: '182px',
  h: '114px'
};
export default {
  name: 'StFaceUpload',
  serviceInject: function serviceInject() {
    return {
      oss: OssService,
      appConfig: AppConfig,
      messageService: MessageService
    };
  },
  modals: {
    FaceRecognition: FaceRecognition
  },
  props: {
    numLimit: {
      type: Number,
      default: 1
    },

    /**
     * 上传按钮的文字
     */
    placeholder: {
      type: String,
      default: '上传照片'
    },

    /**
     * 图片及上传按钮的宽度
     */
    width: {
      type: String,
      default: defaultSize.w
    },

    /**
     * 图片及上传按钮的宽度
     */
    height: {
      type: String,
      default: defaultSize.h
    },

    /**
     * 初始图片列表 example: [{ image_id: 10000, image_key: 'http://styd-saas-test.oss-cn-shanghai.aliyuncs.com/image/pLOFb5kCPN4gPQ8H',image_url:'http://...' }]
     */
    list: {
      type: Array,
      default: function _default() {
        return [];
      }
    },

    /**
     * 缩略图filter选项 同imgFilter选项
     */
    filterOptions: {
      type: Object,
      default: function _default() {
        return {};
      }
    },

    /**
     * 后端字段映射 image_id,image_key,image_url
     */
    props: {
      type: Object,
      default: function _default() {
        return {
          image_id: 'image_id',
          image_key: 'image_key',
          image_url: 'image_url'
        };
      }
    },
    faceEditAuth: {
      type: Boolean,
      default: true
    },
    actionAuth: {
      type: Boolean,
      default: true
    }
  },
  data: function data() {
    return {
      fileList: this.list,
      isLoading: false,
      progress: 0,
      isShowFaceUpload: true
    };
  },
  computed: {
    imageId: function imageId() {
      return this.props.image_id;
    },
    imageKey: function imageKey() {
      return this.props.image_key;
    },
    imageUrl: function imageUrl() {
      return this.props.image_url;
    },
    sizeStyle: function sizeStyle() {
      return "width: ".concat(this.width, ";  height: ").concat(this.height);
    },
    computedFilterOptions: function computedFilterOptions() {
      var w = parseInt(this.width) * 2;
      var h = parseInt(this.height) * 2;
      return Object.assign({
        w: w,
        h: h
      }, this.filterOptions);
    }
  },
  watch: {
    list: {
      handler: function handler(newList) {
        console.log('newList', newList);
        this.isShowFaceUpload = this.list.length < this.numLimit;
        this.fileList = this.list;
      },
      immediate: true
    }
  },
  methods: {
    handlerOpenFaceModal: function handlerOpenFaceModal() {
      this.$modalRouter.push({
        name: 'face-recognition',
        props: {
          fileList: this.fileList
        },
        on: {
          change: this.onChange,
          processChange: this.processChange,
          loadingChange: this.loadingChange
        }
      });
    },
    onDel: function onDel() {
      var fileList = this.fileList;
      var deletedFile = fileList.splice(0, 1);
      this.fileList = fileList;
      this.$emit('delete', deletedFile[0]);
      this.$emit('change', this.fileList);
    },
    onChange: function onChange(list) {
      this.fileList = list;
      this.isShowFaceUpload = this.fileList.length < this.numLimit;
      this.$emit('change', this.fileList);
    },
    processChange: function processChange(processNum) {
      this.progress = processNum;
    },
    loadingChange: function loadingChange(status) {
      this.isLoading = status;
    }
  }
};
