import { OssService } from '@/services/oss.service';
import { MessageService } from '@/services/message.service';
export default {
  bem: {
    pem: 'brand-setting-mina-pem-upload'
  },
  data: function data() {
    return {
      loading: false,
      imageUrl: '',
      key: '',
      name: ''
    };
  },
  serviceInject: function serviceInject() {
    return {
      oss: OssService,
      messageService: MessageService
    };
  },
  methods: {
    beforeUpload: function beforeUpload(file) {
      console.log(file);
      var isLt5M = file.size / 1024 / 1024 < 5;

      if (!/\.pem$/.test(file.name) || !isLt5M) {
        this.messageService.error({
          content: '仅支持.pem格式文件，大小不超过5M'
        });
        return false;
      }

      return true;
    },
    upload: function upload(data) {
      var _this = this;

      this.loading = true;
      this.imageUrl = '';
      this.oss.put({
        file: data.file,
        business: 'file',
        isPrivate: true
      }).subscribe({
        next: function next(val) {
          _this.key = val.fileKey;
          _this.name = data.file.name;

          _this.$emit('success', val.fileKey);

          _this.messageService.success({
            content: '上传成功'
          });

          _this.loading = false;
        },
        error: function error(val) {
          _this.messageService.error({
            content: "Error ".concat(val.message)
          });

          _this.loading = false;
        }
      });
    }
  }
};
