import { TeamScheduleScheduleService } from '@/views/pages/shop/product/course/schedule/team/service#/schedule.service';
export default {
  name: 'CopySchedule',
  serviceInject: function serviceInject() {
    return {
      teamScheduleScheduleService: TeamScheduleScheduleService
    };
  },
  rxState: function rxState() {
    return {
      loading: this.teamScheduleScheduleService.loading$
    };
  },
  data: function data() {
    return {
      timeType: 'week',
      moment: moment,
      show: false,
      disabled: true,
      form: this.$form.createForm(this),
      applyStartDate: moment().week(moment().week()).startOf('week'),
      applyEndDate: moment(),
      addTime: 0,
      copyDefaulValue: []
    };
  },
  methods: {
