import StImageUpload from '@/views/biz-components/st/image-upload/image-upload.vue';
import { cloneDeep } from 'lodash-es';
import { UserService } from '@/services/user.service';
var className = ['first custom', 'second custom', 'third custom', 'fourth custom'];
export default {
  name: 'StInvitationBgRadio',
  bem: {
    cardRadio: 'invitation-bg-radio'
  },
  components: {
    StImageUpload: StImageUpload
  },
  serviceInject: function serviceInject() {
    return {
      userService: UserService
    };
  },
  rxState: function rxState() {
    return {
      pluginEnums: this.userService.getOptions$('plugin.invite_poster')
    };
  },
  created: function created() {
    this.init();
  },
  computed: {
    bgIndex: function bgIndex() {
      return this.radioIndex === 0 ? 4 : this.radioIndex - 1;
    },
    cardBgList: function cardBgList() {
      var arr = [];
      this.invite_poster.forEach(function (item, index) {
        item.className = className[index];
        item.index = ++index;
        arr.push(item);
      });
      return arr;
    }
  },
  data: function data() {
    return {
      // 截图参数对象
      cropperModal: {
        cropper: {
          aspectRatio: 3 / 5
        }
      },
      maskOptions: {
        flag: true,
        width: 750,
        height: 1334,
        maskUrl: 'https://styd-saas-test.oss-cn-shanghai.aliyuncs.com/image/default/bg-invitation-4.png'
      },
      invite_poster: this.pluginEnums,
      // 备份
      list: [],
      // radioIndex
      radioIndex: 1,
      // 选择的cardBg
      cardBg: {
        image_id: 0,
        image_key: this.pluginEnums[0].image_key,
        image_url: this.pluginEnums[0].image_url,
        index: 1
      },
      // 自定义cardBg
      customCardBg: {
        image_id: 0,
        image_key: '',
        image_url: '',
        index: 0
      },
      // 回显的自定义bg
      fileList: []
    };
  },
  watch: {
    value: {
      handler: function handler(newVal) {
        this.setdata(newVal);
      },
      deep: true
    },
    radioIndex: function radioIndex(newVal) {
      if (newVal === 0) {
        this.cardBg = cloneDeep(this.customCardBg);
      } else {
        var i = newVal;
        --i;
        this.cardBg = cloneDeep(this.invite_poster[i]);
        this.cardBg.index = newVal;
      }

      this.$emit('input', this.cardBg);
      this.$emit('change', this.cardBg);
    }
  },
  props: {
    value: {
      type: Object,
      default: function _default() {
        return {
          image_id: 0,
          image_key: '',
          image_url: '',
          index: 1
        };
      }
    }
  },
  mounted: function mounted() {
    this.setRadioColor();
  },
  methods: {
    init: function init() {
      this.list = cloneDeep(this.invite_poster);
      this.setdata(this.value);
    },
    setRadioColor: function setRadioColor() {
      var innerHTML = '';
      this.cardBgList.forEach(function (i) {
        innerHTML += "\n          .invitation-bg-radio .".concat(i.className.split('custom')[0], "\n          .ant-radio-inner::after {\n            background-color: ").concat(i.color, ";\n          }\n        ");
      });
      var style = document.createElement('style');
      style.innerHTML = innerHTML;
      document.head.appendChild(style);
    },
    setdata: function setdata(data) {
      this.radioIndex = data.index;

      if (data.index === 0) {
        this.customCardBg = cloneDeep(data);

        if (data.image_url) {
          this.fileList = [{
            image_id: data.image_id,
            image_url: data.image_url
          }];
        }
      } else {
        this.invite_poster = cloneDeep(this.list);
        var i = data.index;
        --i;
        this.invite_poster[i].image_id = data.image_id;
      }
    },
    imageUploadChange: function imageUploadChange(fileList) {
      if (fileList.length) {
        this.customCardBg = {
          image_id: fileList[0].image_id,
          image_key: fileList[0].image_key,
          image_url: fileList[0].image_url,
          image_host: fileList[0].image_host,
          index: 0
        };
      } else {
        this.fileList = [];
        this.customCardBg = {
          image_id: 0,
          image_key: '',
          image_url: '',
          image_host: '',
          index: 0
        };
      }

      this.cardBg = cloneDeep(this.customCardBg);
      this.$emit('input', this.cardBg);
      this.$emit('change', this.cardBg);
    }
  }
};
