  },
  bem: {
    b: 'page-add-staff',
    bstep: 'page-add-staff-steps',
    bHeader: 'default-brand-header'
  },
  components: {
    EditDetailBasicsInfo: EditDetailBasicsInfo,
    // 编辑基础信息
    EditDetailDetailedInfo: EditDetailDetailedInfo,
    // 编辑详细信息
    EditDetailCoachInfo: EditDetailCoachInfo // 编辑教练信息

  },
  computed: {
    isShowCoach: function isShowCoach() {
      if (this.staffInfo.identity.length === 0) return false;
      return this.staffInfo.identity.includes(3) || this.staffInfo.identity.includes(4);
    },
    isPrivateCoach: function isPrivateCoach() {
      if (this.staffInfo.identity.length === 0) return false;
      return this.staffInfo.identity.includes(4);
    },
    id: function id() {
      return this.staffInfo.staff_id;
    }
  },
  watch: {
    isPrivateCoach: function isPrivateCoach(newVal) {
      if (newVal) {
        this.stepsSpan = 12;
        this.stepArr.pop();
      }
    }
  },
  data: function data() {
    return {
      stepsSpan: 18,
      currentIndex: 0,
      stepArr: [{
        title: '基础信息',
        key: 0
      }, {
        title: '详细信息',
        key: 1
      }, {
        title: "".concat(this.$c('coach'), "\u4FE1\u606F"),
        key: 2
      }]
    };
  },
  mounted: function mounted() {
    var currentIndex = this.$searchQuery.currentIndex;

    if (!this.isPrivateCoach) {
      this.stepsSpan = 12;
      this.stepArr.pop();
    }

    if (currentIndex) this.currentIndex = Number(currentIndex);
  },
  methods: {
    updateStaffInfo: function updateStaffInfo() {
      this.editService.editStaffInfo(this.id).subscribe();
    },
    onDetailInfoSave: function onDetailInfoSave(data) {
      var _this = this;

      if (!this.isShowCoach) delete data.data.is_show;
      this.editService.updateDetailedInfo(this.id, data.data).subscribe(function () {
        if (!_this.isPrivateCoach) {
          _this.$router.push({
            name: 'brand-staff-department'
          });
        } else {
          _this.updateStaffInfo();

          _this.goNext();
        }
      });
    },
    onCoachInfoSave: function onCoachInfoSave(data) {
      var _this2 = this;

      this.editService.updateCoachInfo(this.id, data.data).subscribe(function () {
        _this2.$router.push({
          name: 'brand-staff-department'
        });
      });
    },
    goNext: function goNext() {
      if (this.currentIndex < 2) {
        this.currentIndex = this.currentIndex + 1;
      }
    },
    onBack: function onBack(step) {
      this.currentIndex = this.currentIndex - step;
    },
    changeStep: function changeStep(step) {
      cloneDeep;
      this.currentIndex = cloneDeep(step);
    }
  }
};
