import CreatePersonalCourse from "./components#/edit/create-personal-course";
import SetShopCoach from "./components#/edit/set-shop-coach";
import SetSellPrice from "./components#/edit/set-sell-price";
import { EditService } from "./edit.service";
export default {
  components: {
    CreatePersonalCourse: CreatePersonalCourse,
    // 创建私教课程
    SetShopCoach: SetShopCoach,
    // 设置上课门店及教练
    SetSellPrice: SetSellPrice // 设置售卖价格

  },
  serviceInject: function serviceInject() {
    return {
      editService: EditService
    };
  },
  rxState: function rxState() {
    return {
      info: this.editService.info$
    };
  },
  data: function data() {
    return {
      currentIndex: 0,
      stepArr: [{
        title: '编辑私教课',
        key: 0
      }, {
        title: "\u8BBE\u7F6E\u552E\u5356\u95E8\u5E97\u53CA".concat(this.$c('coach')),
        key: 1
      }, {
        title: '设置售卖价格',
        key: 2
      }]
    };
  },
  methods: {
    goNext: function goNext() {
      if (this.currentIndex < 2) {
        this.currentIndex = this.currentIndex + 1;
      }
    },
    changeStep: function changeStep(step) {
      this.currentIndex = step;
    }
  }
};
