/**
 * json 数据转 AntDesign tree
 * @param {Array} list
 * @param {Object} opts
 * @param {Number} n
 */
export function json2AntDesignTreeData(list) {
  var opts = arguments.length > 1 && arguments[1] !== undefined ? arguments[1] : {};
  var n = arguments.length > 2 && arguments[2] !== undefined ? arguments[2] : 0;
  var defaultOpts = {
    keyRegExp: /^\d+$/,
    keyName: ''
  };
  opts = Object.assign(defaultOpts, opts);
  return list.map(function (item) {
    var _n = n;
    var isLeaf = true;
    var _opts = opts,
        keyRegExp = _opts.keyRegExp,
        keyName = _opts.keyName;

    for (var i in item) {
      console.log(item[i]);

      if (keyName) {
        item.key = item[keyName];
      } else {
        if (keyRegExp.test(item[i])) {
          item.key = item[i];
        } else {
          item.key = item.id;
        }
      }

      if (typeof item[i] === 'string') {
        item.title = item[i];
      }

      if (item[i] instanceof Array) {
        isLeaf = false;
        item.children = item[i];
        item.children = json2AntDesignTreeData(item.children, opts, ++_n);
      }
    }

    item.key = JSON.stringify({
      isLeaf: isLeaf,
      key: item.key
    });
    return item;
  });
}
