import { InfoService } from "./info.service";
import { btnOptions } from "./info.config";
import moment from 'moment';
import SoldCoursePackageUpgrade from '@/views/biz-modals/sold/course/package/upgrade';
import SoldCourseFreeze from '@/views/biz-modals/sold/course/freeze';
import SoldCourseRefund from '@/views/biz-modals/sold/course/refund';
import SoldCourseSurplus from '@/views/biz-modals/sold/course/surplus';
import SoldCourseTransfer from '@/views/biz-modals/sold/course/transfer';
import SoldCourseValid from '@/views/biz-modals/sold/course/valid';
import SoldCoursePackageUpdateExpirationDate from '@/views/biz-modals/sold/course/package/update-expiration-date';
import SoldCoursePackageDeduction from '@/views/biz-modals/sold/course/package/deduction';
import SoldCoursePackageMembeChange from '@/views/biz-modals/sold/course/package/member-change';
import DealGathering from '@/views/biz-modals/sold/deal/gathering';
import SoldCoursePackageUpdateRights from '@/views/biz-modals/sold/course/package/update-rights';
import { PACKAGE_TYPE, IS_LIMIT } from '@/constants/sold/package';
import useInternalNoteActions from '@/hooks/shop/sold-internal-note-actions.hook';
import contractActions from '@/hooks/contract.hook';
import { PrintService } from '@/services/print.service';
export default {
  name: 'PageShopSoldCoursePackageInfo',
  bem: {
    basic: 'page-shop-sold-info'
  },
  modals: {
    SoldCourseFreeze: SoldCourseFreeze,
    SoldCourseRefund: SoldCourseRefund,
    SoldCourseSurplus: SoldCourseSurplus,
    SoldCourseValid: SoldCourseValid,
    DealGathering: DealGathering,
    SoldCoursePackageDeduction: SoldCoursePackageDeduction,
    SoldCoursePackageUpdateExpirationDate: SoldCoursePackageUpdateExpirationDate,
    SoldCoursePackageMembeChange: SoldCoursePackageMembeChange,
    SoldCoursePackageUpgrade: SoldCoursePackageUpgrade,
    SoldCourseTransfer: SoldCourseTransfer,
    SoldCoursePackageUpdateRights: SoldCoursePackageUpdateRights
  },
  data: function data() {
    return {
      PACKAGE_TYPE: PACKAGE_TYPE,
      IS_LIMIT: IS_LIMIT
    };
  },
  serviceInject: function serviceInject() {
    return {
      infoService: InfoService,
      printService: PrintService
    };
  },
  hooks: function hooks() {
    return {
      internalNoteActions: useInternalNoteActions(),
      contractActions: contractActions(this)
    };
  },
  rxState: function rxState() {
    return {
      packageInfo: this.infoService.packageInfo$,
      authTabs: this.infoService.authTabs$,
      auth: this.infoService.auth$
    };
  },
  computed: {
    btnOptions: btnOptions,
    rangeType: function rangeType() {
      return this.packageInfo.course_range ? this.packageInfo.course_range.range_type : 1;
    },
    rangeColumns: function rangeColumns() {
      return [{
        title: '课程类型',
        dataIndex: 'course_type_name',
        key: 'course_type_name'
      }, {
        title: '课程名称',
        dataIndex: 'course_name',
        key: 'course_name',
        width: '120px'
      }, {
        title: '每次预约扣除次数',
        dataIndex: 'course_weights',
        key: 'course_weights'
      }, {
        title: '上课门店',
        dataIndex: 'support_shop',
        key: 'support_shop'
      }];
    },
    courseColumns: function courseColumns() {
      var list = [{
        title: '课程类型',
        dataIndex: 'course_name',
        key: 'course_name'
      }, {
        title: '上课门店',
        dataIndex: 'support_shop',
        key: 'support_shop',
        width: '120px',
        scopedSlots: {
          customRender: 'support_shop'
        }
      }, {
        title: '节数',
        dataIndex: 'course_num',
        key: 'course_num'
      }, {
        title: '课时费',
        dataIndex: 'course_price',
        key: 'course_price'
      }, {
        title: '小计',
        dataIndex: 'pay_amount',
        key: 'pay_amount'
      }];

      if (this.rangeType === 3) {
        list.splice(3, 1);
      }

      if (this.packageInfo.package_type !== PACKAGE_TYPE.ALL_COURSE) {
        if (this.packageInfo.is_limit === IS_LIMIT.NO_LIMIT) {
          list.splice(2, 1);
          list.splice(3, 1);
        }

        list.splice(1, 1);
      }

      return list;
    },
    courseTabData: function courseTabData() {
      var data = [];
      var team = this.packageInfo.course_range ? this.packageInfo.course_range.team : {};
      var personal = this.packageInfo.course_range ? this.packageInfo.course_range.personal : {};
      var support_shop = '';

      if (team.support_shop) {
        support_shop = team.support_shop;
      }

      if (personal.support_shop) {
        support_shop = personal.support_shop;
      }

      if (team.course_name) {
        data.push({
          key: 0,
          support_shop: support_shop,
          course_name: team.course_name,
          course_num: team.course_num,
          course_price: team.course_price,
          pay_amount: team.pay_amount
        });
      }

      if (personal.course_name) {
        data.push({
          key: 1,
          support_shop: support_shop,
          course_name: personal.course_name,
          course_num: personal.course_num,
          course_price: personal.course_price,
          pay_amount: personal.pay_amount
        });
      }

      if (this.packageInfo.course_range.range_type === 2) {
        data = this.packageInfo.course_range.courses;
      }

      return data;
    },
    // 范围内课程团体课
    innerColumnsTeam: function innerColumnsTeam() {
      var list = [{
        title: '课程名称',
        dataIndex: 'course_name',
        key: 'course_name',
        scopedSlots: {
          customRender: 'course_name'
        },
        width: '120px'
      }, {
        title: '上课门店',
        dataIndex: 'support_shop',
        key: 'support_shop',
        width: '120px',
        scopedSlots: {
          customRender: 'support_shop'
        }
      }];
      return list;
    },
    innerDataTeam: function innerDataTeam() {
      return this.packageInfo.course_range.team.courses;
    },
    // 范围内课程私教课
    innerColumnsPersonal: function innerColumnsPersonal() {
      var list = [{
        title: '课程名称',
        dataIndex: 'course_name',
        key: 'course_name',
        width: '120px',
        scopedSlots: {
          customRender: 'course_name'
        }
      }, {
        title: '上课门店',
        dataIndex: 'support_shop',
        key: 'support_shop',
        width: '120px',
        scopedSlots: {
          customRender: 'support_shop'
        }
      }, {
        title: "".concat(this.$c('coach'), "\u7B49\u7EA7"),
        dataIndex: 'levels',
        key: 'levels',
        scopedSlots: {
          customRender: 'levels'
        }
      }];
      return list;
    },
    innerDataPersonal: function innerDataPersonal() {
      var innerData = [];

      for (var i = 0; i < this.packageInfo.course_range.personal.courses.length; i++) {
        innerData.push({
          key: i,
          support_shop: this.packageInfo.course_range.personal.courses[i].support_shop,
          course_name: this.packageInfo.course_range.personal.courses[i].course_name,
          levels: this.packageInfo.course_range.personal.courses[i].levels.join(',')
        });
      }

      return innerData;
    },
    // 固定课程
    innerColumnsFix: function innerColumnsFix() {
      var list = [{
        title: '课程名称',
        dataIndex: 'course_name',
        key: 'course_name'
      }, {
        title: '上课门店',
        dataIndex: 'support_shop',
        key: 'support_shop',
        scopedSlots: {
          customRender: 'support_shop'
        }
      }, {
        title: '节数',
        dataIndex: 'course_num_init',
        key: 'course_num_init'
      }, {
        title: '课时费',
        dataIndex: 'course_price',
        key: 'course_price'
      }, {
        title: '总价',
        dataIndex: 'course_amount',
        key: 'course_amount'
      }];
      return list;
    },
    innerDataFixPersonal: function innerDataFixPersonal() {
      var innerData = [];

      for (var i = 0; i < this.packageInfo.course_range.personal.courses.length; i++) {
        innerData.push({
          key: i,
          course_name: this.packageInfo.course_range.personal.courses[i].course_name,
          support_shop: this.packageInfo.course_range.personal.courses[i].support_shop,
          course_num_init: this.packageInfo.course_range.personal.courses[i].course_num_init,
          course_price: this.packageInfo.course_range.personal.courses[i].course_price,
          course_amount: this.packageInfo.course_range.personal.courses[i].course_amount,
          levels: this.packageInfo.course_range.personal.courses[i].levels.join(',')
        });
      }

      return this.packageInfo.course_range.personal.courses;
    },
    innerDataFixTeam: function innerDataFixTeam() {
      var innerData = [];

      for (var i = 0; i < this.packageInfo.course_range.team.courses.length; i++) {
        innerData.push({
          key: i,
          course_name: this.packageInfo.course_range.team.courses[i].course_name,
          support_shop: this.packageInfo.course_range.team.courses[i].support_shop,
          course_num_init: this.packageInfo.course_range.team.courses[i].course_num_init,
          course_price: this.packageInfo.course_range.team.courses[i].course_price,
          course_amount: this.packageInfo.course_range.team.courses[i].course_amount
        });
      }

      return innerData;
    }
  },
  methods: {
    moment: moment,
    onEditDate: function onEditDate() {
      var id = this.packageInfo.id;
      this.$modalRouter.push({
        name: 'sold-course-package-update-expiration-date',
        props: {
          id: id
        },
        on: {
          success: function success() {// this.successTip()
          }
        }
      });
    },
    onMemberChange: function onMemberChange() {
      var id = this.packageInfo.id;
      this.$modalRouter.push({
        name: 'sold-course-package-membe-change',
        props: {
          id: id
        },
        on: {
          success: function success() {// this.successTip()
          }
        }
      });
    },
    onReduceTimes: function onReduceTimes() {
      var _this = this;

      var id = this.packageInfo.id;
      this.$modalRouter.push({
        name: 'sold-course-package-deduction',
        props: {
          id: id
        },
        on: {
          success: function success() {
            _this.successTip();
          }
        }
      });
    },
    // 修改剩余课时
    onSurplus: function onSurplus() {
      var _this2 = this;

      // 后端返回的时间戳
      var time = this.packageInfo.effective_state ? "".concat(moment(this.packageInfo.course_buy_time * 1000).format('YYYY-MM-DD HH:mm'), " \u81F3 ").concat(moment(this.packageInfo.course_end_time * 1000).format('YYYY-MM-DD HH:mm')) : '--';
      var data = {
        id: this.infoService.id,
        courseName: this.packageInfo.course_name,
        time: time
      };
      this.$modalRouter.push({
        name: 'sold-course-surplus',
        props: {
          courseType: 'coursePackage',
          courseData: data
        },
        on: {
          success: function success() {
            _this2.$router.reload();
          }
        }
      });
    },
    // 跳转合同
    toContract: function toContract() {
      var record = this.packageInfo;
      var url = "".concat(window.location.origin, "/common/contract-preview?id=").concat(record.order_id);
      window.open(url);
    },
    onUpgrade: function onUpgrade() {
      var _this3 = this;

      var id = this.packageInfo.id;
      this.$modalRouter.push({
        name: 'sold-course-package-upgrade',
        props: {
          id: id
        },
        on: {
          success: function success(res) {
            if (res.type === 'create') {
              // 创建订单成功
              _this3.$router.reload();

              _this3.onClear();
            } else if (res.type === 'createPay') {
              // 创建订单成功 并且到支付页面
              _this3.$modalRouter.push({
                name: 'deal-gathering',
                props: {
                  order_id: res.orderId,
                  type: 'package'
                },
                on: {
                  success: function success() {
                    _this3.$router.reload();

                    _this3.onClear();
                  }
                }
              });
            }
          }
        }
      });
    },
    // 冻结
    onFreeze: function onFreeze() {
      var _this4 = this;

      this.$modalRouter.push({
        name: 'sold-course-freeze',
        props: {
          type: 'package',
          id: this.infoService.id,
          courseName: this.packageInfo.course_name,
          courseNum: this.packageInfo.remain_course_num,
          courseEndTime: moment(this.packageInfo.course_end_time * 1000),
          time: "".concat(moment(this.packageInfo.course_buy_time * 1000).format('YYYY-MM-DD HH:mm'), " \u81F3 ").concat(moment(this.packageInfo.course_end_time * 1000).format('YYYY-MM-DD HH:mm'))
        },
        on: {
          success: function success() {
            _this4.$router.reload();
          }
        }
      });
    },
    // 取消冻结
    onUnfreeze: function onUnfreeze() {
      var _this5 = this;

      this.$confirm({
        title: '提示',
        content: '是否取消冻结？',
        maskClosable: true,
        onOk: function onOk() {
          return _this5.infoService.unFreeze(_this5.infoService.id).toPromise().then(function () {
            _this5.$router.reload();
          });
        }
      });
    },
    // 转让
    onTransfer: function onTransfer() {
      var _this6 = this;

      this.$modalRouter.push({
        name: 'sold-course-transfer',
        props: {
          type: 'package',
          id: this.infoService.id
        },
        on: {
          success: function success() {
            _this6.$router.reload();
          }
        }
      });
    },
    onValid: function onValid() {
      var _this7 = this;

      this.$modalRouter.push({
        name: 'sold-course-valid',
        props: {
          info: this.packageInfo
        },
        on: {
          success: function success() {
            _this7.$router.reload();
          }
        }
      });
    },
    // 退款
    onRefund: function onRefund() {
      var _this8 = this;

      this.$modalRouter.push({
        name: 'sold-course-refund',
        props: {
          id: this.infoService.id,
          type: 'package'
        },
        on: {
          success: function success() {
            _this8.$router.reload();
          }
        }
      });
    },
    // 对内备注
    onInternalNote: function onInternalNote() {
      this.internalNoteActions.onOpenModal({
        id: this.infoService.id
      });
    },

    /**
     * 修改合同备注
     */
    onEditContractDescription: function onEditContractDescription() {
      var _this$packageInfo = this.packageInfo,
          order_id = _this$packageInfo.order_id,
          description = _this$packageInfo.description;
      this.contractActions.editDescription({
        order_id: order_id,
        description: description
      });
    },
    updateRight: function updateRight() {
      var id = this.packageInfo.id;
      this.$modalRouter.push({
        name: 'sold-course-package-update-rights',
        props: {
          id: id
        }
      });
    }
  }
};
