import { UserService } from '@/services/user.service';
import { ListService } from "./list.service";
import MarkteingPluginTitle from "../../components#/marketing-title";
import tableMixin from '@/mixins/table.mixin';
import { columns, shopListColumns } from "./list.config";
import { TYPE } from '@/constants/marketing/plugin';
import BrandMarketingBind from '@/views/biz-modals/brand/marketing/bind';
import useShare from '@/hooks/marketing/share.hook';
import CardBrandMemberShopTable from '@/views/biz-modals/card/brand-member/shop-table';
export default {
  name: 'PageBrandMarketingPluginGroupList',
  mixins: [tableMixin],
  bem: {
    basic: 'page-brand-plugin-group-list'
  },
  modals: {
    BrandMarketingBind: BrandMarketingBind,
    CardBrandMemberShopTable: CardBrandMemberShopTable
  },
  serviceInject: function serviceInject() {
    return {
      listService: ListService,
      userService: UserService
    };
  },
  rxState: function rxState() {
    return {
      list: this.listService.list$,
      page: this.listService.page$,
      loading: this.listService.loading$,
      auth: this.listService.auth$,
      info: this.listService.info$,
      groupBuyEnums: this.listService.groupBuyEnums$,
      shopList: this.listService.shopList$,
      shopPage: this.listService.shopPage$,
      isAuth: this.listService.isAuth$
    };
  },
  hooks: function hooks() {
    return {
      share: useShare()
    };
  },
  data: function data(vm) {
    return {
      TYPE: TYPE,
      activityName: '',
      activityStatus: -1,
      columns: columns,
      shopListColumns: shopListColumns,
      modalShow: false,
      // 控制选择门店
      title: '选择门店',
      groupId: ''
    };
  },
  computed: {},
  mounted: function mounted() {
    this.setSearchData();
  },
  watch: {
    query: function query(newVal) {
      this.setSearchData();
    }
  },
  methods: {
    onConfigHandle: function onConfigHandle() {
      this.$router.push({
        path: '/brand/setting/mina/index'
      });
    },
    // 选择门店推广
    onSelect: function onSelect(record) {
      var _this = this;

      this.listService.getSharePosterInfo(this.groupId, {
        shop_id: record.id
      }).subscribe(function (res) {
        _this.modalShow = false;
        var isAuth = _this.isAuth;
        var shsInfo = {
          person_num: _this.info.group_sum,
          name: _this.info.product_name,
          qrcode_url: _this.info.qrcode_base,
          logo: _this.info.brand_logo,
          brand_name: _this.info.brand_name,
          price: _this.info.price,
          image: _this.info.product_logo
        };

        _this.share.poster({
          isAuth: isAuth,
          shsInfo: shsInfo,
          hasQrCodeBtn: true,
          shsPath: '/saas-mina/groupbuy'
        });
      });
    },
    // 设置状态&名称
    setSearchData: function setSearchData() {
      var _this$$searchQuery = this.$searchQuery,
          activity_name = _this$$searchQuery.activity_name,
          activity_status = _this$$searchQuery.activity_status;
      this.activityName = activity_name;
      this.activityStatus = activity_status || -1;
    },
    // 活动发布
    onRelease: function onRelease(record) {
      var that = this;
      this.$confirm({
        title: '提示',
        content: '确定发布该活动?发布成功后，用户即可在小程序端看到该活动。',
        onOk: function onOk() {
          that.listService.releaseGroup(record.id).subscribe(function (res) {
            that.$router.reload();
          });
        },
        onCancel: function onCancel() {}
      });
    },
    // 数据
    onData: function onData(record) {
      this.$router.push({
        path: './data',
        query: {
          id: record.id
        }
      });
    },
    // 选择门店
    onGeneralize: function onGeneralize(record) {
      var _this2 = this;

      this.groupId = record.id;
      this.listService.getShopList(record.id, {
        size: record.size || 20,
        current_page: record.current_page || 1
      }).subscribe(function (res) {
        _this2.modalShow = true;
      });
    },
    // 门店分页
    onShopTableChange: function onShopTableChange(data) {
      console.log(data);
      this.onGeneralize({
        id: this.groupId,
        size: data.pageSize,
        current_page: data.current
      });
    },
    // 编辑列表
    onEdit: function onEdit(record) {
      var typeId = record.product_type.id;
      var id = record.id;

      switch (typeId) {
        case 3:
          this.$router.push({
            path: './edit-personal',
            query: {
              id: id
            }
          });
          break;

        case 2:
          this.$router.push({
            path: './edit-deposit',
            query: {
              id: id
            }
          });
          break;

        case 1:
          this.$router.push({
            path: './edit-member',
            query: {
              id: id
            }
          });
          break;

        case 4:
          this.$router.push({
            path: './edit-package-course',
            query: {
              id: id
            }
          });
          break;

        default:
      }
    },
    // 结束活动
    onStop: function onStop(record) {
      var that = this;
      this.$confirm({
        title: '提示',
        content: '确定停止该活动吗？活动停止后，未成团订单将自动关闭并退款。',
        onOk: function onOk() {
          that.listService.stopGroup(record.id).subscribe(function (res) {
            that.$router.reload();
          });
        },
        onCancel: function onCancel() {}
      });
    },
    // 新增活动
    onAddGroup: function onAddGroup() {
      this.$router.push({
        path: './choose'
      });
    }
  },
  components: {
    MarkteingPluginTitle: MarkteingPluginTitle
  }
};
