import { UserLevelService } from "./user-level.service";
import { MessageService } from '@/services/message.service';
import SelectCondition from "./components#/select-condition";
import SelectRights from "./components#/select-rights";
export default {
  bem: {
    b: 'page-user-level'
  },
  serviceInject: function serviceInject() {
    return {
      userLevelService: UserLevelService,
      messageService: MessageService
    };
  },
  rxState: function rxState() {
    var userLevelService = this.userLevelService;
    return {
      list: userLevelService.list$,
      info: userLevelService.info$,
      loading: userLevelService.loading$,
      auth: userLevelService.auth$
    };
  },
  data: function data() {
    return {
      condition: [],
      rights: []
    };
  },
  filters: {
    textFilter: function textFilter(text, list, index) {
      return text + (index < list.length - 1 ? '、' : '');
    }
  },
  components: {
    SelectCondition: SelectCondition,
    SelectRights: SelectRights
  },
  computed: {
    isEdit: function isEdit() {
      return this.$searchQuery.type === 'edit';
    },

    /**
     * 潜在会员
     */
    potential: function potential() {
      return this.list[0];
    },

    /**
     * 正式会员
     */
    member: function member() {
      return this.list[1];
    },

    /**
     * 流失会员
     */
    lost: function lost() {
      return this.list[2];
    }
  },
  methods: {
    onEdit: function onEdit() {
      this.$router.push({
        query: {
          type: 'edit'
        }
      });
    },
    onSave: function onSave() {
      var _this = this;

      var params = this.getData();

      if (!this.inputCheck(params)) {
        this.messageService.error({
          content: '请至少选择一个升级到正式会员所需条件'
        });
        return;
      }

      this.userLevelService.update(params).subscribe(function () {
        _this.messageService.success({
          content: '保存成功'
        });

        _this.$router.push({
          query: {}
        });
      });
    },
    onCancel: function onCancel() {
      this.$router.push({
        query: {}
      });
    },
    onConditionChange: function onConditionChange(val) {
      this.condition = val;
    },
    onRightsChange: function onRightsChange(val) {
      this.rights = val;
    },
    getData: function getData() {
      return {
        condition: this.condition,
        rights: this.rights
      };
    },
    inputCheck: function inputCheck(params) {
      var condition = params.condition;

      if (!condition.length) {
        return false;
      }

      return true;
    }
  }
};
