import { UserService } from '@/services/user.service';
import { ListService } from "./list.service";
import ChangeStaffPostion from "./list#/change-staff-postion";
import { columns } from "./list.config";
import tableMixin from '@/mixins/table.mixin';
import ShopStaffBatchImport from '@/views/biz-modals/shop-staff/batch-import';
import ShopStaffBindCard from '@/views/biz-modals/shop-staff/bind-card';
import ShopStaffLeaveCurrentShop from '@/views/biz-modals/shop-staff/leave-current-shop';
import ShopStaffRePassword from '@/views/biz-modals/shop-staff/re-password';
import ShopStaffSalaryAccountSetting from '@/views/biz-modals/shop-staff/salary-account-setting';
import ShopStaffUpdateStaffPosition from '@/views/biz-modals/shop-staff/update-staff-position';
import StaffUpdateStaffPosition from '@/views/biz-modals/staff/update-staff-position';
export default {
  mixins: [tableMixin],
  modals: {
    ShopStaffBatchImport: ShopStaffBatchImport,
    ShopStaffBindCard: ShopStaffBindCard,
    ShopStaffLeaveCurrentShop: ShopStaffLeaveCurrentShop,
    ShopStaffRePassword: ShopStaffRePassword,
    ShopStaffSalaryAccountSetting: ShopStaffSalaryAccountSetting,
    ShopStaffUpdateStaffPosition: ShopStaffUpdateStaffPosition,
    StaffUpdateStaffPosition: StaffUpdateStaffPosition
  },
  components: {
    ChangeStaffPostion: ChangeStaffPostion
  },
  serviceInject: function serviceInject() {
    return {
      userService: UserService,
      service: ListService
    };
  },
  rxState: function rxState() {
    return {
      staffList: this.service.staffList$,
      page: this.service.page$,
      roleOptions: this.service.roleOptions$,
      loading: this.service.loading$,
      department: this.service.department$,
      identityList: this.service.identityList$,
      workStatusList: this.service.workStatusList$,
      auth: this.service.auth$
    };
  },
  data: function data() {
    return {
      visible: false,
      value: '',
      enums: {},
      modaldata: {}
    };
  },
  computed: {
    columns: columns
  },
  methods: {
    getShopName: function getShopName(record) {
      console.log('get shop name', record);

      if (record.is_super_admin) {
        return '全部';
      }

      return record.shop.filter(Boolean).map(function (item) {
        return item.name;
      }).join(',');
    },
    changeStaffPosition: function changeStaffPosition(id) {
      var _this = this;

      this.userService.getEnums().subscribe(function (res) {
        _this.enums = res.staff;
        _this.visible = true;
        _this.modaldata = {
          nature_work: 1,
          identity: [1, 2]
        };
      });
    },
    selectDepartment: function selectDepartment(e) {
      console.log(e);
      this.value = e + '';
    },
    joinok: function joinok() {
      this.onSearchReset();
    },
    onJoinDepartment: function onJoinDepartment(e) {
      console.log('批量加入部门');
      this.$modalRouter.push({
        name: 'shop-staff-batch-import',
        props: {
          ids: this.selectedRowKeys
        },
        on: {
          change: this.joinok
        }
      });
    },
    onAddStaff: function onAddStaff() {
      this.$router.push({
        name: 'shop-staff-add'
      });
    },
    onExportStaff: function onExportStaff() {
      console.log('导入员工');
    },
    // 详情
    onSearchDetail: function onSearchDetail(e) {
      var identity = e.identity.map(function (ele) {
        return ele.id;
      });
      this.$router.push({
        name: 'shop-staff-info-basic',
        query: {
          id: e.id
        }
      });
    },
    onEdit: function onEdit(e) {
      this.$router.push({
        name: 'shop-staff-edit',
        query: {
          id: e.id
        }
      });
    },
    onChooseDepartment: function onChooseDepartment(e) {
      console.log('选择部门', e);
      this.$router.push({
        query: {
          shop_id: e
        }
      });
    },
    onChooseStaffFn: function onChooseStaffFn(e) {
      console.log('选择员工职能', e);
    },
    onChooseStaffStatus: function onChooseStaffStatus(e) {
      console.log('选择在职状态', e);
      this.$router.push({
        query: {
          work_status: e
        }
      });
    },
    onSearch: function onSearch(e) {
      console.log('搜索员工', e);
    },
    // 清空指纹信息
    onClearFingerprintInfo: function onClearFingerprintInfo(record) {
      var _this2 = this;

      this.$confirm({
        title: '提示信息',
        content: '确认清空指纹信息？',
        onOk: function onOk() {
          _this2.service.deleteStaffFinger(record.id).subscribe();
        }
      });
    },
    // 清空硬件密码
    onClearHardwarePassword: function onClearHardwarePassword(record) {
      var _this3 = this;

      this.$confirm({
        title: '提示信息',
        content: '确认清空硬件密码吗？',
        onOk: function onOk() {
          _this3.service.deleteStaffCabinetPassword(record.id).subscribe();
        }
      });
    }
  }
};
