import h5PhoneComponent from "./h5-phone.component";
import { H5SettingTabbarService } from "./h5-setting-tabbar.service";
import { MessageService } from '@/services/message.service';
import h5ModuleComponent from "./h5-module.component"; // 模块组件

import h5FormTabbarComponent from "./form-components#/h5-form.tabbar.component";
export default {
  name: 'H5SettingTabbarComponent',
  components: {
    h5PhoneComponent: h5PhoneComponent,
    h5ModuleComponent: h5ModuleComponent,
    h5FormTabbarComponent: h5FormTabbarComponent
  },
  bem: {
    h5: 'h5-setting-tabbar-component'
  },
  data: function data() {
    return {
      sliderIndex: 0,
      modulesDataString: [],
      isEditFlag: false
    };
  },
  serviceInject: function serviceInject() {
    return {
      messageService: MessageService,
      h5SettingTabbarService: H5SettingTabbarService
    };
  },
  rxState: function rxState() {
    return {
      modulesData: this.h5SettingTabbarService.modulesData$,
      loading: this.h5SettingTabbarService.loading$
    };
  },
  created: function created() {
    this.init();
  },
  watch: {
    modulesData: {
      handler: function handler(val) {
        this.modulesDataString = JSON.parse(JSON.stringify(val));
      },
      deep: true,
      immediate: true
    }
  },
  methods: {
    init: function init() {
      var _this = this;

      this.h5SettingTabbarService.getShopModules({
        tab_type: '4'
      }).subscribe(function (res) {
        _this.sliderIndex = 0;
      });
    },
    submitForm: function submitForm() {
      var _this2 = this;

      this.$confirm({
        title: '',
        content: '确认将当前设置内容发布到小程序？',
        onOk: function onOk() {
          var info = {
            tab_type: '4',
            module_list: _this2.modulesDataString
          };

          _this2.h5SettingTabbarService.postShopModules({
            info: info
          }).subscribe(function (res) {
            _this2.messageService.success({
              content: '保存成功'
            });

            _this2.editEmit(false);
          });
        }
      });
    },
    editEmit: function editEmit() {
      var flag = arguments.length > 0 && arguments[0] !== undefined ? arguments[0] : true;
      this.isEditFlag = flag;
      this.$emit('change', this.isEditFlag);
    }
  }
};
