import { MarketingTitleService } from "./marketing-title.service";
import BrandMarketingRuleTip from '@/views/biz-modals/brand/marketing/rule-tip';
export default {
  name: 'MarktingPluginTitle',
  bem: {
    basic: 'markting-plugin-title'
  },
  modals: {
    BrandMarketingRuleTip: BrandMarketingRuleTip
  },
  serviceInject: function serviceInject() {
    return {
      marketingTitleService: MarketingTitleService
    };
  },
  props: {
    /**
     * 插件类型 coupon|invite|crowd|lottery 用于请求插件介绍信息
     */
    type: {
      type: Number,
      required: true
    }
  },
  rxState: function rxState() {
    return {
      loading: this.marketingTitleService.loading$,
      info: this.marketingTitleService.info$
    };
  },
  created: function created() {
    this.marketingTitleService.getPluginInfo(this.type).subscribe();
  },
  data: function data() {
    return {
      imgKeys: ['http://styd-saas-test.oss-cn-shanghai.aliyuncs.com/image/IUt_vXTl8zaWGwlO.jpg', 'http://styd-saas-test.oss-cn-shanghai.aliyuncs.com/image/IUt_vXTl8zaWGwlO.jpg', 'http://styd-saas-test.oss-cn-shanghai.aliyuncs.com/image/IUt_vXTl8zaWGwlO.jpg']
    };
  },
  methods: {
    ruleDetail: function ruleDetail() {
      this.$modalRouter.push({
        name: 'brand-marketing-rule-tip',
        props: {
          contents: this.info.regulation
        },
        on: {
          success: function success() {
            console.log('success');
          }
        }
      });
    },
    openTeach: function openTeach() {
      if (this.info.usage_link) {
        window.open(this.info.usage_link);
      }
    },
    openCase: function openCase() {
      if (this.info.usage_link) {
        window.open(this.info.case_link);
      }
    }
  }
};
