import { Injectable, Controller, ServiceRoute } from 'vue-service-app'
import { State } from 'rx-state'
import { BrandPackageApi } from '@/api/v1/package/brand'
import { CommonPackageApi } from '@/api/v1/package/common'
import { tap } from 'rxjs/operators'
import { MessageService } from '@/services/message.service'
import { anyAll } from '@/operators'

@Injectable()
export class EditFixPackageService implements Controller {
  loading$ = new State({})
  info$ = new State({})
  coachList$ = new State({})
  constructor(
    private api: BrandPackageApi,
    private commonApi: CommonPackageApi,
    private msg: MessageService
  ) {}

  getCoachList() {
    return this.commonApi.getCoachList().pipe(
      tap((res: any) => {
        this.coachList$.commit(() => res.list)
      })
    )
  }
  getEditShow(id: any) {
    return this.api.getFixEditShow(id).pipe(
      tap(res => {
        console.log(res.info)
        this.info$.commit(() => res.info)
      })
    )
  }
  edit(query: any) {
    return this.api.editFix(query).pipe(
      tap(res => {
        this.msg.success({ content: '编辑课程包成功' })
      })
    )
  }
  init(id: any) {
    return anyAll(this.getEditShow(id), this.getCoachList())
  }
  beforeEach(to: ServiceRoute) {
    return this.init(to.query.id)
  }
}
