import { Injectable } from 'vue-service-app'
import { State } from 'rx-state'
import { UserService } from '@/services/user.service'
import { remove } from 'lodash-es'
import { map } from 'rxjs/operators'
import { AuthService } from '@/services/auth.service'

@Injectable()
export class FormService {
  // 是否配置了用户端
  appConfig = true
  unitList$ = this.userService.getOptions$('package_course.valid_time_unit')
  auth$ = this.authService.authMap$({
    up: 'brand:product:package_course|up'
  })
  transferUnitList$ = this.userService.getOptions$(
    'package_course.transfer_unit'
  )
  supportSales$ = this.userService
    .getOptions$('package_course.sale_range')
    .pipe(
      map(options => {
        options = options.filter(item => item.value !== 1)
        return options.reverse()
      })
    )
  sellTypeList$ = this.userService.getOptions$('package_course.sale_mode').pipe(
    map(options => {
      if (!this.appConfig) {
        remove(options, i => i.value === 1)
      }
      return options
    })
  )
  loading$ = new State({})
  coachList$ = new State({})
  constructor(
    private userService: UserService,
    private authService: AuthService
  ) {}
}
