import { Injectable, Controller } from 'vue-service-app'
import { State } from 'rx-state'
import { BrandPackageApi } from '@/api/v1/package/brand'
import { CommonPackageApi } from '@/api/v1/package/common'
import { tap } from 'rxjs/operators'
import { MessageService } from '@/services/message.service'

@Injectable()
export class AddFixPackageService implements Controller {
  loading$ = new State({})
  coachList$ = new State({})
  constructor(
    private api: BrandPackageApi,
    private commonApi: CommonPackageApi,
    private msg: MessageService
  ) {}

  getCoachList() {
    return this.commonApi.getCoachList().pipe(
      tap((res: any) => {
        this.coachList$.commit(() => res.list)
      })
    )
  }
  add(query: any) {
    return this.api.addFix(query).pipe(
      tap(res => {
        this.msg.success({ content: '新增课程包成功' })
      })
    )
  }
  addAndUp(query: any) {
    return this.api.addFixOnShelf(query).pipe(
      tap(res => {
        this.msg.success({ content: '新增并上架课程包成功' })
      })
    )
  }
  beforeEach() {
    return this.getCoachList()
  }
}
