import { Injectable } from 'vue-service-app'
import { UserService } from '@/services/user.service'
import { map } from 'rxjs/operators'
@Injectable()
export class ReserveIsLimitService {
  // 是否配置了用户端
  reserveLimitDay$ = this.userService
    .getOptions$('package_course.reserve_limit_day')
    .pipe(
      map(res => {
        return res.map(item => {
          item.label = item.label.includes('每')
            ? item.label
            : `每${item.label}`
          return item
        })
      })
    )
  reserveIsLimit$ = this.userService.getOptions$(
    'package_course.reserve_is_limit'
  )
  constructor(private userService: UserService) {}
}
