/**
 * @param {string} keyPath
 * @description 解析webpack key路径
 * @example
 * var key = './plugin/a/b.vue'
 * parsedKeyPath(key) // => {
 *  ext:'.vue',
 *  name: 'b',
 *  base: 'b.vue',
 *  dir： 'plugin/a',
 *  entry: 'plugin/a/b'
 *  dir_dash:'plugin-a',
 *  entry_dash: 'plugin-a-b'
 * }
 *
 */
export var parse = function parse(keyPath) {
  keyPath = keyPath.slice(2);
  var lastDotIndex = keyPath.lastIndexOf('.');
  var lastSlashIndex = keyPath.lastIndexOf('/');
  var ext = '';
  var dir = '';
  var name = '';
  var base = '';
  var entry = '';
  ext = keyPath.slice(lastDotIndex);

  if (lastSlashIndex > -1) {
    dir = keyPath.slice(0, lastSlashIndex);
  }

  name = keyPath.slice(lastSlashIndex + 1, lastDotIndex);
  base = name + ext;
  entry = dir ? dir + '/' + name : name;
  return {
    name: name,
    ext: ext,
    base: base,
    dir: dir,
    entry: entry,
    dir_dash: dir.replace(/\//g, '-'),
    entry_dash: entry.replace(/\//g, '-')
  };
};
