export default {
  name: 'h5FormAssetsdataComponent',
  bem: {
    module: 'h5-form-assetsdata-component'
  },
  components: {},
  props: {
    assetsData: {
      type: Object,
      default: function() {
        return {}
      }
    }
  },
  watch: {
    assetsData: {
      handler(newVal) {
        this.list = newVal.content.list
      },
      immediate: true,
      deep: true
    }
  },
  data() {
    return {
      list: [],
      canDrag: true
    }
  },
  methods: {
    draggChange() {
      this.$emit('draggChange', this.list)
    },
    onClick(index) {
      console.log('----', this.list, index)
      let displayTotal = 0
      this.list.forEach(val => {
        if (val.is_display) {
          displayTotal++
        }
      })
      if (displayTotal === 1 && this.list[index].is_display) {
        return
      }
      this.list[index].is_display = +!this.list[index].is_display
      this.changeData()
    },
    changeData() {
      this.$emit('change')
    }
  }
}
