import { cloneDeep } from 'lodash-es'
import { PersonalTeamScheduleCommonService as CommonService } from '@/views/pages/shop/product/course/schedule/personal-team/service#/common.service'
import { PersonalTeamScheduleScheduleService as ScheduleService } from '@/views/pages/shop/product/course/schedule/personal-team/service#/schedule.service'
import { MessageService } from '@/services/message.service'
import { columns } from './add-in-batch.config'
export default {
  name: 'AddCourseScheduleBatch',
  serviceInject() {
    return {
      commonService: CommonService,
      scheduleService: ScheduleService,
      msg: MessageService
    }
  },
  rxState() {
    const commonService = this.commonService
    return {
      courseOptions: commonService.courseOptions$,
      courseCoachOptions: commonService.courseCoachOptions$
    }
  },
  data() {
    const data = []
    for (let i = 0; i < 100; i++) {
      if (i === 0) {
        data.push({
          key: 0,
          editable: true,
          start_time: moment(),
          course_id: '',
          coach_id: '',
          limit_num: 0
        })
      }
    }
    this.cacheData = data.map(item => ({ ...item }))
    return {
      data,
      obj: {
        key: 0,
        editable: true,
        start_time: '',
        course_id: '',
        coach_id: '',
        show: {}
      },
      show: false
    }
  },
  filters: {
    formatDate(val) {
      return val ? moment(val) : undefined
    }
  },
  computed: {
    columns
  },
  methods: {
    validateForm(form) {
      if (!form.start_time) {
        this.msg.error({ content: '排期开始时间不能为空' })
        return false
      }
      if (!form.course_id) {
        this.msg.error({ content: '请选择课程' })
        return false
      }
      if (!form.coach_id) {
        this.msg.error({ content: `请选择课程${this.$c('coach')}` })
        return false
      }
      if (!form.limit_num) {
        this.msg.error({ content: '请输入上课人数' })
        return false
      }
      if (!form.course_fee) {
        this.msg.error({ content: '请输入课时费' })
        return false
      }
      return true
    },
    onOkSaveForm() {
      let data = cloneDeep(this.data)
      data = data
        .filter((item, index) => {
          return index !== 0
        })
        .map(item => {
          delete item.show
          delete item.key
          delete item.editable
          item.start_time = moment(item.start_time)
            .format('YYYY-MM-DD HH:mm')
            .valueOf()
          item.limit_num = +item.limit_num
          item.course_fee = +item.course_fee
          return item
        })
      if (!data.length) {
        this.msg.error({ content: '请填写排期！' })
        return
      }
      this.scheduleService
        .addScheduleInBatch(data)
        .subscribe(this.onSubmitSuccess)
    },
    handleChange(value, key, column) {
      const newData = [...this.data]
      const target = newData.filter(item => key === item.key)[0]
      if (column === 'course_id') {
        this.commonService.getCourseCoachList(value).subscribe()
        target.coach_id = ''
      }
      if (target) {
        target[column] = value
        if (column === 'start_time') {
          target[column] = value.format('YYYY-MM-DD HH:mm')
        }
        console.log(target[column])
        this.data = newData
      }
    },
    edit(key) {
      const newData = [...this.data]
      const target = newData.filter(item => key === item.key)[0]
      if (target) {
        target.editable = true
        this.data = newData
      }
    },
    deleteData(key) {
      const newData = [...this.data]
      const target = newData.filter(item => key === item.key)[0]
      if (target) {
        target.editable = true
        this.data = newData.filter(item => {
          return item.key !== key
        })
      }
    },
    getShowTableData(data) {
      data.key = Math.random()
      data.show = {}
      data.show.start_time_show = moment(data.start_time).format(
        'YYYY-MM-DD HH:mm'
      )
      data.show.course_id_show = this.getOptionName(
        data.course_id,
        this.courseOptions,
        'name'
      )
      data.show.coach_id_show = this.getOptionName(
        data.coach_id,
        this.courseCoachOptions,
        'name'
      )
      return data
    },
    getOptionName(id, options, name) {
      let option = []
      options.forEach(ele => {
        if (ele.id === id) {
          option = ele[name]
        }
      })
      return option
    },
    save(key) {
      let newData = [...this.data]
      const target = newData.filter(item => key === item.key)[0]
      if (!this.validateForm(target)) {
        return ''
      }
      if (key === 0 && target) {
        delete target.editable
        delete newData[0].editable
        newData[0] = this.getShowTableData(newData[0])
        newData.unshift({
          key: 0,
          editable: true,
          start_time: moment(),
          course_id: '',
          coach_id: '',
          limit_num: 0,
          show: {}
        })
        this.data = newData
        this.cacheData = newData.map(item => ({ ...item }))
        return
      }
      if (target) {
        delete target.editable
        if (newData.length) {
          newData.forEach(item => {
            if (item.key === key && key !== 0) {
              item = this.getShowTableData(item)
            }
          })
        }
        this.data = newData
        this.cacheData = newData.map(item => ({ ...item }))
      }
    },
    cancel(key) {
      const newData = [...this.data]
      const target = newData.filter(item => key === item.key)[0]
      if (target) {
        Object.assign(
          target,
          this.cacheData.filter(item => key === item.key)[0]
        )
        delete target.editable
        this.data = newData
      }
    },
    onSubmitSuccess() {
      this.show = false
      this.$emit('ok')
    }
  }
}
