import StImageUpload from '@/views/biz-components/st/image-upload/image-upload.vue'
export default {
  name: 'h5FormCoursedataComponent',
  bem: {
    module: 'h5-form-coursedata-component'
  },
  components: { StImageUpload },
  props: {
    courseData: {
      type: Object,
      default: function() {
        return {}
      }
    }
  },
  watch: {
    courseData: {
      handler(newVal) {
        this.list = newVal.content
      },
      immediate: true,
      deep: true
    }
  },
  data() {
    return {
      list: []
    }
  },
  methods: {
    onImageChange(image) {
      if (image.length === 0) return
      this.list.image.image_url = image[0].image_url
      this.changeData()
    },
    onImageDelete(image) {
      this.changeData()
    },
    changeData() {
      this.$emit('change')
    }
  }
}
