export default {
  props: {
    layout: {
      type: [String, Number],
      default: 1
    }
  },
  data() {
    return {
      show: false,
      currentLayout: 1
    }
  },
  watch: {
    layout: {
      handler(newV) {
        if (newV) {
          this.currentLayout = newV
        }
      },
      immediate: true
    }
  },
  methods: {
    cancel() {
      this.show = false
    },
    save() {
      this.show = false
      this.$emit('success', this.currentLayout)
    }
  }
}
