export default {
  name: 'AddCourseUnlimitTable',
  bem: {
    add: 'page-shop-add-fix-package',
    b: 'add-course-fix-table'
  },
  props: {
    initInfo: {
      type: Object,
      default: () => {
        return {
          // 是否支持团体课 0为不支持 1为支持
          is_team: 0,
          // 团体课总节数
          team_times: undefined,
          // 团体课单价
          team_unit_price: undefined,
          // 是否支持私教课 0为不支持 1为支持
          is_personal: 0,
          // 私教课总节数
          personal_times: undefined,
          // 私教课单价
          personal_unit_price: undefined
        }
      }
    }
  },
  watch: {
    packageData: {
      handler: function(newValue, oldValue) {
        if (!newValue.is_team && !newValue.is_personal) {
          this.$emit('change', null)
          return
        }
        if (
          newValue.is_team &&
          !newValue.is_personal &&
          !newValue.team_times &&
          !newValue.team_unit_price
        ) {
          this.$emit('change', null)
          return
        }
        if (
          newValue.is_personal &&
          !newValue.is_team &&
          !newValue.personal_times &&
          !newValue.personal_unit_price
        ) {
          this.$emit('change', null)
          return
        }
        this.$emit('change', newValue)
      },
      deep: true
    }
  },
  data() {
    return {
      packageData: {
        // 是否支持团体课 0为不支持 1为支持
        is_team: 0,
        // 团体课总节数
        team_times: undefined,
        // 团体课单价
        team_unit_price: undefined,
        // 是否支持私教课 0为不支持 1为支持
        is_personal: 0,
        // 私教课总节数
        personal_times: undefined,
        // 私教课单价
        personal_unit_price: undefined
      },
      // 课程范围是否未输入过
      courseIsFirstInput: true
    }
  },
  created() {
    this.packageData.is_team = this.initInfo.is_team
    this.packageData.is_personal = this.initInfo.is_personal
    this.packageData.personal_unit_price = this.initInfo.personal_unit_price
    this.packageData.team_unit_price = this.initInfo.team_unit_price
    this.packageData.team_times = this.initInfo.team_times
    this.packageData.personal_times = this.initInfo.personal_times
  },
  methods: {
    // checkboxChange
    teamCheckboxChange(e) {
      this.packageData.is_team = +e.target.checked
      this.courseIsFirstInput = false
    },
    personalCheckboxChange(e) {
      this.packageData.is_personal = +e.target.checked
      this.courseIsFirstInput = false
    }
  },
  computed: {
    // 团课小计
    team_total() {
      if (+this.packageData.team_times && +this.packageData.team_unit_price) {
        return (
          (this.packageData.team_times *
            (this.packageData.team_unit_price * 10)) /
          10
        )
      } else {
        return 0
      }
    },
    // 私教小计
    personal_total() {
      if (
        +this.packageData.personal_times &&
        +this.packageData.personal_unit_price
      ) {
        return (
          (this.packageData.personal_times *
            (this.packageData.personal_unit_price * 10)) /
          10
        )
      } else {
        return 0
      }
    },
    // 总价
    all_total() {
      let teamTotal = this.packageData.is_team ? this.team_total : 0
      let personalTotal = this.packageData.is_personal ? this.personal_total : 0
      return teamTotal + personalTotal
    }
  }
}
