import { AssignShopService } from './assign-shop.service'
import { ruleOptions } from './assign-shop.config'
export default {
  serviceInject() {
    return {
      assignShopService: AssignShopService
    }
  },
  rxState() {
    const { loading$, types$, shopList$ } = this.assignShopService
    return {
      loading: loading$,
      types: types$,
      shopList: shopList$
    }
  },
  decorators() {
    return this.form.addDecorators(this.ruleOptions)
  },
  bem: {
    b: 'biz-assign-shop'
  },
  props: {
    /**
     * 1当前门店/2多店/3全店
     */
    type: {
      type: Number,
      default: 1
    },
    shopIds: {
      type: Array,
      default() {
        return []
      }
    },
    form: {
      type: Object,
      default: () => {
        return {}
      }
    },
    label: {
      type: String,
      default: '入场场馆'
    },
    labelWidth: {
      type: String,
      default: '88px'
    },
    labelGutter: {
      type: String,
      default: '24px'
    },
    required: {
      type: Boolean,
      default: true
    },
    disabled: {
      type: Boolean,
      default: false
    }
  },
  data() {
    return {
      currentType: 1,
      /**
       * 多门店
       */
      MULTI_SHOP: 2
    }
  },
  computed: {
    ruleOptions,
    decorators() {
      return this.form.addDecorators(this.ruleOptions)
    },
    isShowShopList() {
      /**
       * 多店
       */
      return this.currentType === this.MULTI_SHOP
    }
  },
  watch: {
    disabled() {
      this.form.validateFields(['currentShopIds'], { force: true })
    },
    type(val) {
      this.currentType = val
    },
    shopIds: {
      handler(val) {
        this.form.setFieldsValue({
          currentShopIds: val
        })
      },
      deep: true
    }
  },
  created() {
    this.assignShopService.getShopList().subscribe()
  },
  mounted() {
    this.init()
  },
  methods: {
    init() {
      const { type, shopIds } = this
      this.currentType = type
      this.$nextTick(() => {
        this.form.setFieldsValue({
          currentShopIds: shopIds
        })
      })
    },
    onShopIdsChange(currentShopIds) {
      this.form.setFieldsValue({
        currentShopIds
      })
      this.onChange()
    },
    onTypeChange(e) {
      this.currentType = e.target.value
      this.$nextTick(() => {
        this.form.validateFields(['currentShopIds'], { force: true })
      })
      this.onChange()
    },
    onChange() {
      const { currentType } = this
      const currentShopIds = this.form.getFieldValue('currentShopIds')
      this.$emit('change', currentType, currentShopIds)
    }
  }
}
